import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Dropdown, Image } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';

const CustomNavbar = () => {
  const [vendor, setVendor] = useState(null); // Holds vendor data
  const [storeLogo, setStoreLogo] = useState(null);

  useEffect(() => {
    // Fetch vendor details if logged in
    const fetchVendorData = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage
      if (token) {
        try {
          const response = await axios.get('/api/vendors/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          const vendorData = response.data.data;
          setVendor(vendorData);
          setStoreLogo(vendorData.logoUrl);
        } catch (error) {
          console.error('Error fetching vendor data:', error);
        }
      }
    };
    fetchVendorData();
  }, []);

  return (
    <Navbar bg="dark" variant="dark" expand="lg" className="px-3">
      {/* Left Side: Logo */}
      <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
        <Image
          src="../assets/images/logo.png" // Replace with your logo path
          alt="logo"
          width="30"
          height="30"
          className="d-inline-block align-top me-2"
        />
        <span>weedhurry</span>
      </Navbar.Brand>

      {/* Right Side: Dropdown Menu */}
      <Nav className="ms-auto">
        <Dropdown align="end">
          <Dropdown.Toggle
            as="div" // Custom toggle without a button appearance
            id="dropdown-menu"
            className="text-white border-0 p-0"
            style={{ cursor: 'pointer' }}
          >
            <i className="bi bi-list" style={{ fontSize: '1.5rem' }}></i>
          </Dropdown.Toggle>

          <Dropdown.Menu>
            {!vendor ? (
              // If no vendor is logged in
              <Dropdown.Item as={Link} to="/login">
                Login
              </Dropdown.Item>
            ) : (
              // If vendor is logged in
              <>
                <div className="d-flex align-items-center p-3">
                  <Image
                    src={storeLogo || '/default-avatar.png'} // Replace with default avatar
                    alt="Store Logo"
                    roundedCircle
                    width="40"
                    height="40"
                    className="me-2"
                  />
                  <div>
                    <strong>{vendor.dispensaryName}</strong>
                    <div className="text-muted">
                      {vendor.firstName} {vendor.lastName?.charAt(0)}.
                    </div>
                  </div>
                </div>
                <Dropdown.Divider />
                <Dropdown.Item as={Link} to="/account">
                  Account
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/logout">
                  Logout
                </Dropdown.Item>
              </>
            )}
          </Dropdown.Menu>
        </Dropdown>
      </Nav>
    </Navbar>
  );
};

export default CustomNavbar;
