import React from 'react';
import ProductCard from './ProductCard';
import { useNavigate } from 'react-router-dom';

const VendorCard = ({ vendor }) => {
  const navigate = useNavigate();

  const handleViewMenu = () => {
    navigate(`/vendor/${vendor._id}/menu`);
  };

  return (
    <div className="vendor-card shadow p-3 mb-4 rounded">
      {/* Vendor Header */}
      <div className="d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center">
          <img
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={vendor.dispensaryName}
            className="rounded"
            onClick={handleViewMenu}
            style={{
              width: '44px',
              height: '44px',
              objectFit: 'cover',
              marginRight: '10px',
              cursor: 'pointer',
            }}
          />
          <div>
            <h6 className="mb-1">{vendor.dispensaryName}</h6>
            <p className="text-muted mb-0">Minimum Order: ${vendor.minOrder || 'N/A'}</p>
          </div>
        </div>
      </div>

      {/* Filtered Products Preview */}
      <div className="products-preview mt-3">
        {vendor.products.length ? (
          <div className="d-flex overflow-auto">
            {vendor.products.map((product) => (
              <ProductCard
                key={product._id}
                product={product}
                vendorName={vendor.dispensaryName}
                logoUrl={vendor.logoUrl}
                vendorId={vendor._id}
              />
            ))}
          </div>
        ) : (
          <p className="text-muted">No products available</p>
        )}
      </div>
    </div>
  );
};

export default VendorCard;
