import React, { useContext, useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { VendorProvider, VendorContext } from '../context/VendorContext';
import VendorDetails from '../components/VendorDetails';
import MenuDisplay from '../components/MenuDisplay';
import CategoryFilter from '../components/CategoryFilter';
import Footer from '../components/Footer';
import './VendorMenuPage.css';

const VendorMenuPage = () => {
  const { vendorId } = useParams();
  return (
    <VendorProvider vendorId={vendorId}>
      <VendorMenuContent />
    </VendorProvider>
  );
};

const VendorMenuContent = () => {
  const { vendor, products, loading, error } = useContext(VendorContext);
  const { state } = useLocation();
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilters, setActiveFilters] = useState({
    sativa: false,
    hybrid: false,
    indica: false,
  });
  const [orderType, setOrderType] = useState(state?.initialOrderType || 'Delivery');
  const [animate, setAnimate] = useState(false);

  // Normalize product data to ensure consistent structure
  const normalizeProduct = (product) => ({
    _id: product._id,
    name: product.name,
    brand: product.brand,
    category: product.category,
    description: product.description,
    strain: product.variation?.strain || product.strain || 'N/A',
    thcContent: product.variation?.thcContent || product.thcContent || null,
    price: product.variation?.price || product.price || 0,
    salePrice: product.variation?.salePrice || product.salePrice || null,
    image: product.variation?.image || product.image || "/placeholder-image.png",
    amount: product.variation?.amount || product.amount || 'N/A',
    status: product.status,
    vendorId: vendor?._id
  });

  // Normalize products when they're received from context
  useEffect(() => {
    if (products?.length) {
      const normalizedProducts = products.map(normalizeProduct);
      console.log('Normalized products:', normalizedProducts);
      setFilteredProducts(normalizedProducts);
    }
  }, [products, vendor?._id]);

  // Apply filters to normalized products
  useEffect(() => {
    if (!products?.length) return;

    const applyFilters = () => {
      let updatedProducts = filteredProducts;
      console.log('Starting filter process with products:', updatedProducts);

      // Category filter
      if (selectedCategory !== 'All') {
        updatedProducts = updatedProducts.filter(
          (product) => product.category === selectedCategory
        );
        console.log('After category filter:', updatedProducts);
      }

      // Strain classification filter
      const activeClassifications = Object.entries(activeFilters)
        .filter(([_, isActive]) => isActive)
        .map(([classification]) => classification.toLowerCase());

      if (activeClassifications.length > 0) {
        updatedProducts = updatedProducts.filter((product) => {
          const strain = (product.strain || '').toLowerCase();
          return activeClassifications.some((filter) => {
            if (filter === 'sativa') return strain.includes('sat');
            if (filter === 'hybrid') return strain.includes('hyb');
            if (filter === 'indica') return strain.includes('ind');
            return false;
          });
        });
        console.log('After strain filter:', updatedProducts);
      }

      // Search term filter
      if (searchTerm.trim()) {
        updatedProducts = updatedProducts.filter((product) =>
          [product.name, product.brand, product.strain]
            .filter(Boolean)
            .some((field) =>
              field.toLowerCase().includes(searchTerm.trim().toLowerCase())
            )
        );
        console.log('After search filter:', updatedProducts);
      }

      setFilteredProducts(updatedProducts);
    };

    applyFilters();
  }, [selectedCategory, activeFilters, searchTerm, products]);

  // Animation effect
  useEffect(() => {
    setAnimate(true);
  }, []);

  const handleCategoryChange = (category) => setSelectedCategory(category);
  const handleSearch = (term) => setSearchTerm(term);
  const handleFilter = (filters) => setActiveFilters(filters);

  if (error) return <div>Error: {error}</div>;
  if (loading) return <div>Loading...</div>;

  return (
    <div
      className={`vendor-menu-page ${animate ? 'slide-in' : ''}`}
      style={{
        backgroundColor: '#F8F9FA',
        minHeight: '100vh',
        padding: '0px',
        margin: '0',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}
    >
      <div className="shared-card">
        <VendorDetails 
          vendor={vendor} 
          orderType={orderType} 
          setOrderType={setOrderType} 
        />

        <div className='category-filter'>
          <CategoryFilter
            selectedCategory={selectedCategory}
            onCategoryChange={handleCategoryChange}
            onSearch={handleSearch}
            onFilter={handleFilter}
          />
        </div>

        <MenuDisplay
          products={filteredProducts}
          vendorName={vendor?.dispensaryName}
          logoUrl={vendor?.logoUrl}
          vendorId={vendor?._id}
          orderType={orderType}
        />
      </div>
      <Footer />
    </div>
  );
};

export default VendorMenuPage;
