import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddressPage = () => {
  const [address, setAddress] = useState('');
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (address.trim()) {
      // Save the address in state, context, or localStorage
      localStorage.setItem('userAddress', address);

      // Navigate to the nearby vendors page
      navigate('/nearby');
    } else {
      alert('Please enter a valid address.');
    }
  };

  return (
    <div>
      <h1>Welcome to WeedHurry</h1>
      <p>Please enter your address to find nearby vendors:</p>
      <input
        type="text"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        placeholder="Enter your address"
      />
      <button onClick={handleSubmit}>Find Vendors</button>
    </div>
  );
};

export default AddressPage;
