import React from 'react';
import { Card, Table, Button } from 'react-bootstrap';
import { useLocation } from 'react-router-dom';

const OrderConfirmationPage = () => {
  const location = useLocation();
  const { order, cartItemsSummary } = location.state || {};

  // Handle missing order or cartItemsSummary gracefully
  if (!order || !cartItemsSummary) {
    return <div className="container mt-5">Order details not available.</div>;
  }

  return (
    <div className="container mt-5">
      <Card className="shadow-sm">
        <Card.Header className="bg-primary text-white">
          <h3 className="mb-0">Order Confirmation</h3>
        </Card.Header>
        <Card.Body>
          {/* Order Details */}
          <div className="mb-4">
            <h5>Order Details</h5>
            <p><strong>Order ID:</strong> {order._id}</p>
            <p><strong>Status:</strong> {order.status}</p>
            <p><strong>Total Amount:</strong> ${order.total.toFixed(2)}</p>
          </div>

          {/* Delivery Address */}
          <div className="mb-4">
            <h5>Delivery Address</h5>
            <p>{order.deliveryAddress.street}</p>
            <p>{order.deliveryAddress.city}, {order.deliveryAddress.state} {order.deliveryAddress.zip}</p>
          </div>

          {/* Items Table */}
          <div>
            <h5>Items</h5>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Quantity</th>
                  <th>Price</th>
                </tr>
              </thead>
              <tbody>
                {cartItemsSummary.map((item, index) => (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.quantity}</td>
                    <td>${item.price}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>

          {/* Continue Shopping Button */}
          <Button
            variant="primary"
            className="mt-4"
            onClick={() => window.location.href = '/'} // Redirect to home or another appropriate page
          >
            Continue Shopping
          </Button>
        </Card.Body>
      </Card>
    </div>
  );
};

export default OrderConfirmationPage;
