import React, { useRef, useEffect } from 'react';
import { Card, Form } from 'react-bootstrap';

const AddressInputCard = ({ onAddressUpdate, onClose }) => {
  const inputRef = useRef(null);

  useEffect(() => {
    if (window.google && inputRef.current) {
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'], // Limit suggestions to addresses
        componentRestrictions: { country: 'us' }, // Restrict to the US
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          const location = {
            lat: place.geometry.location.lat(),
            lng: place.geometry.location.lng(),
            address: place.formatted_address,
          };

          onAddressUpdate(location); // Pass location to parent
          onClose(); // Close the modal
        }
      });
    }
  }, []);

  return (
    <Card className="p-3">
      <Card.Body>
        <Card.Title>Enter Your Address</Card.Title>
        <Form.Group controlId="addressInput">
          <Form.Control
            ref={inputRef}
            type="text"
            placeholder="Start typing your address..."
            aria-label="Enter your address"
          />
        </Form.Group>
      </Card.Body>
    </Card>
  );
};

export default AddressInputCard;
