import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuthState] = useState({ token: null, vendorId: null });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const initializeAuth = async () => {
            const storedAuth = JSON.parse(localStorage.getItem('auth'));
            if (storedAuth?.refreshToken) {
                try {
                    const response = await axios.post('/api/auth/refresh-token', {}, {
                        headers: { Authorization: `Bearer ${storedAuth.refreshToken}` },
                    });
                    const updatedAuth = { ...storedAuth, token: response.data.accessToken };
                    setAuth(updatedAuth);
                } catch (error) {
                    console.error('Token refresh failed:', error);
                    handleLogout();
                }
            }
            setLoading(false);
        };
        initializeAuth();
    }, []);

    const setAuth = (authData) => {
        localStorage.setItem('auth', JSON.stringify(authData));
        setAuthState(authData);
    };

    const handleLogout = () => {
        localStorage.removeItem('auth');
        setAuthState({ token: null, vendorId: null });
    };

    return (
        <AuthContext.Provider value={{ auth, setAuth, logout: handleLogout, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
