import React, { useEffect, useState } from 'react';
import PickupMap from './PickupMap';

const NearbyPickupPage = () => {
  const [vendors, setVendors] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const fetchVendors = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${BASE_URL}/vendors/nearby?lat=34.0522&lng=-118.2437`); // Example coordinates for LA
      const data = await response.json();

      if (!data.vendors) throw new Error('No vendors found.');

      setVendors(data.vendors);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVendors();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading map...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : (
        <PickupMap dispensaries={vendors} />
      )}
    </div>
  );
};

export default NearbyPickupPage;
