import React, { useState, useEffect } from 'react';
import { Button, ButtonGroup, Container, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './TestMenu.css'; // Make sure this file includes styles for horizontal scrolling

// Custom category order and mapping
const categoryMapping = {
    'FLOWER': 'Flower',
    'Sativa Flower': 'Flower',
    'Indica Flower': 'Flower',
    'Pre-roll': 'Pre-roll',
    'Single Pre-roll': 'Pre-roll',
    'CARTRIDGE': 'Vape',
    'Vape Cartridge': 'Vape',
    'EDIBLE': 'Edible',
    'PILL': 'Edible',
    'BEVERAGE': 'Edible',
    'TINCTURE': 'Concentrate',
    'EXTRACT': 'Concentrate',
    'CONCENTRATE': 'Concentrate',
    'default': 'Misc' // Any unmatched categories fall under "Misc"
};

// Fixed category button order
const customCategoryOrder = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Concentrate', 'Misc'];

const TestMenu = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('All');

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                const mappedProducts = response.data.product_list.map(product => {
                    const mappedCategory = categoryMapping[product.category_type] || categoryMapping.default;
                    return { ...product, frontendCategory: mappedCategory };
                });
                setProducts(mappedProducts);
                setFilteredProducts(mappedProducts);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchProducts();
    }, []);

    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
        setFilteredProducts(
            category === 'All'
                ? products
                : products.filter(product => product.frontendCategory === category)
        );
    };

    return (
        <Container>
            <h2 className="mb-4">Vendor Menu</h2>

            {/* Scrollable Category Button Group */}
            <div className="category-buttons mb-3 overflow-auto d-flex">
                <ButtonGroup>
                    <Button variant="outline-secondary" onClick={() => handleCategoryChange('All')}>
                        All
                    </Button>
                    {customCategoryOrder.map((category) => (
                        <Button
                            key={category}
                            variant="outline-secondary"
                            onClick={() => handleCategoryChange(category)}
                        >
                            {category}
                        </Button>
                    ))}
                </ButtonGroup>
            </div>

            {/* Display Products by Selected Category */}
            <Row>
                {filteredProducts
                    .filter((product) =>
                        selectedCategory === 'All' || product.frontendCategory === selectedCategory
                    )
                    .map((product) => (
                        <Col xs={6} md={4} lg={3} key={product.product_id} className="mb-4">
                            <Card>
                                <Card.Img
                                    variant="top"
                                    src={product.e_commerce?.primary_image || '/placeholder.jpg'}
                                    alt={product.product_configurable_fields.name}
                                />
                                <Card.Body>
                                    <Card.Title>{product.product_configurable_fields.name}</Card.Title>
                                    <Card.Text>{product.product_configurable_fields.brand}</Card.Text>
                                    <Button variant="primary">
                                        ${product.pricing.price_sell} + Add to Cart
                                    </Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
            </Row>
        </Container>
    );
};

export default TestMenu;
