// In NearbyVendorsPage.js
import React, { useEffect, useState } from 'react';
import VendorCard from './VendorCard';
import Footer from './Footer';
import api from '../api/api';

const NearbyVendorsPage = ({ searchQuery }) => {
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'https://api.weedhurry.com';

  // Function to calculate distance in miles
  const calculateDistanceInMiles = (lat1, lng1, lat2, lng2) => {
    const toRadians = (degree) => (degree * Math.PI) / 180;
    const R = 3963; // Earth's radius in miles
    const dLat = toRadians(lat2 - lat1);
    const dLng = toRadians(lng2 - lng1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) * Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }
  }, []);

  const fetchVendors = async () => {
    setLoading(true);
    try {
        const address = localStorage.getItem('userAddress');
        if (!address) throw new Error('Please provide a valid address.');

        console.log('Fetching with address:', address);

        const geocodeResponse = await fetch(
            `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
                address
            )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
        );
        const geocodeData = await geocodeResponse.json();

        console.log('Geocode response:', geocodeData);

        if (!geocodeData.results || geocodeData.results.length === 0) {
            throw new Error('Could not geocode address');
        }

        const { lat, lng } = geocodeData.results[0].geometry.location;
        console.log('Coordinates:', { lat, lng });

        const { data: vendorsData } = await api.get('/vendors/nearby', {
            params: { lat, lng }
        });

        console.log('Vendors response:', vendorsData);

        if (!vendorsData.vendors) {
            throw new Error('No vendors found');
        }

        // Rest of your code...
    } catch (err) {
        console.error('Detailed error:', {
            message: err.message,
            response: err.response?.data,
            stack: err.stack
        });
        setError(err.message);
    } finally {
        setLoading(false);
    }
};

  useEffect(() => {
    fetchVendors();
  }, []);

  const filteredVendors = vendors
    .filter((vendor) => 
      vendor.dispensaryType === 'Delivery' || vendor.dispensaryType === 'Pickup & Delivery'
    )
    .map((vendor) => {
      const filteredProducts = vendor.products?.filter((product) => {
        const matchesProductName = product.name?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesBrand = product.brand?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesStrain = product.strain?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        return matchesProductName || matchesBrand || matchesStrain;
      });

      // Preserve all vendor data including milesAway while filtering products
      return { ...vendor, products: filteredProducts || [] };
    });

  return (
    <div className="container-fluid p-0">
      <div className="vendor-list">
        {loading ? (
          <p>Loading vendors...</p>
        ) : error ? (
          <p className="text-danger">{error}</p>
        ) : filteredVendors.length ? (
          filteredVendors.map((vendor) => (
            <div key={vendor._id} className="vendors-page vendor-card-wrapper">
              <VendorCard vendor={vendor} />
            </div>
          ))
        ) : (
          <p></p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default NearbyVendorsPage;
