import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, ButtonGroup } from 'react-bootstrap';
import axios from 'axios';

const customCategoryOrder = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Topical', 'Merch'];

const TestMenu = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('All');

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                setProducts(response.data);  // Use backend-mapped categories directly
                setFilteredProducts(response.data);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchProducts();
    }, []);

    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
        setFilteredProducts(
            category === 'All'
                ? products
                : products.filter(product => product.category === category) // Use mapped category directly
        );
    };

    return (
        <Container>
            <h2 className="my-4">Vendor Menu</h2>
            <ButtonGroup className="d-flex overflow-auto">
                <Button variant="outline-secondary" onClick={() => handleCategoryChange('All')}>
                    All
                </Button>
                {customCategoryOrder.map((category) => (
                    <Button key={category} variant="outline-secondary" onClick={() => handleCategoryChange(category)}>
                        {category}
                    </Button>
                ))}
            </ButtonGroup>
            {customCategoryOrder.map((category) => (
                <div key={category} className="my-4">
                    {(selectedCategory === 'All' || selectedCategory === category) && (
                        <>
                            <h4>{category}</h4>
                            <Row>
                                {filteredProducts
                                    .filter(product => product.category === category) // Directly using mapped category
                                    .map((product) => (
                                        <Col xs={12} md={6} lg={4} key={product.productId} className="mb-4">
                                            <Card>
                                                <Card.Img variant="top" src={product.primaryImage || '/placeholder.jpg'} />
                                                <Card.Body>
                                                    <Card.Title>{product.name}</Card.Title>
                                                    <Card.Text>{product.brand}</Card.Text>
                                                    <Button variant="primary">${product.price} + Add to Cart</Button>
                                                </Card.Body>
                                            </Card>
                                        </Col>
                                    ))}
                            </Row>
                        </>
                    )}
                </div>
            ))}
        </Container>
    );
};

export default TestMenu;
