import React from 'react';
import { Container } from 'react-bootstrap';
import VendorInfoCard from './VendorInfoCard';
import VendorLogoCard from './VendorLogoCard';
import DispensaryInfoCard from './DispensaryInfoCard';
import DeactivateAccount from './DeactivateAccount';

const AccountPage = () => {
  return (
    <Container className="py-4 d-flex flex-column">
      {/* Page Title */}
      <h2 className="text-center mb-4">Account</h2>

      {/* First Row: VendorInfoCard and VendorLogoCard */}
      <div className="d-flex flex-wrap flex-column flex-md-row mb-4">
        {/* Vendor Info Card */}
        <div className="flex-grow-1 mb-3 mb-md-0 me-md-3">
          <VendorInfoCard />
        </div>
        {/* Vendor Logo Card */}
        <div className="flex-grow-1">
          <VendorLogoCard />
        </div>
      </div>

      {/* Second Row: DispensaryInfoCard */}
      <div className="d-flex justify-content-md-start mb-4">
        <div className="w-100 w-md-auto">
          <DispensaryInfoCard />
        </div>
      </div>

      {/* Deactivate Account Button */}
      <div className="d-flex justify-content-start mt-auto">
        <DeactivateAccount />
      </div>
    </Container>
  );
};

export default AccountPage;
