import React, { useState, useContext, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card, Container } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { calculateTotal, cartItems, clearCart } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    birthdate: '',
  });

  const [savedAddress, setSavedAddress] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [newAddress, setNewAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState(''); // Track selected payment method
  const [vendorPaymentMethods, setVendorPaymentMethods] = useState([]); // Store vendor's accepted payments
  const inputRef = useRef(null); // Ref for the address input field

  const navigate = useNavigate();

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  useEffect(() => {
    const address = localStorage.getItem('userAddress');
    if (address) setSavedAddress(address);

    const storedContactInfo = JSON.parse(localStorage.getItem('contactInfo'));
    if (storedContactInfo) setContactInfo(storedContactInfo);

    // Fetch vendor payment methods
    const fetchVendorPaymentMethods = async () => {
      const vendorId = cartItems[0]?.vendorId; // Assuming all items belong to one vendor
      if (vendorId) {
        try {
          const response = await fetch(`http://localhost:5001/api/vendors/${vendorId}`);
          const vendor = await response.json();
          setVendorPaymentMethods(vendor.data.acceptedPayments || []);
        } catch (error) {
          console.error('Error fetching vendor payment methods:', error);
        }
      }
    };

    fetchVendorPaymentMethods();
  }, [cartItems]);

  useEffect(() => {
    if (isEditing && inputRef.current) {
      // Initialize Google Autocomplete
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'],
      });

      // Listen for place selection
      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place?.formatted_address) {
          setNewAddress(place.formatted_address);
        }
      });
    }
  }, [isEditing]);

  useEffect(() => {
    localStorage.setItem('contactInfo', JSON.stringify(contactInfo));
  }, [contactInfo]);



  const handleBirthdateChange = (e) => {
    const formattedValue = formatBirthdate(e.target.value);
    setContactInfo({ ...contactInfo, birthdate: formattedValue });
  };

  const handleSaveAddress = () => {
    localStorage.setItem('userAddress', newAddress);
    setSavedAddress(newAddress);
    setIsEditing(false);
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone || !contactInfo.birthdate) {
      alert('Please fill out all required fields.');
      return;
    }
    if (!savedAddress) {
      alert('Please add a delivery address.');
      return;
    }
    if (!paymentMethod) {
      alert('Please select a payment method.');
      return;
    }

    const payload = {
      phone: contactInfo.phone,
      firstName: contactInfo.firstName,
      lastName: contactInfo.lastName,
      email: contactInfo.email,
      birthday: contactInfo.birthdate,
      totalAmount: estimatedTotal,
      vendorId: cartItems[0]?.vendorId,
      orderDetails: {
        type: 'DELIVERY',
        order_source: 'ECOMMERCE',
        order_status: 'AWAITING_PROCESSING',
        delivery_address: savedAddress,
        items: cartItems.map((item) => ({
          productId: item.productId || item._id,
          name: item.name,
          category: item.category,
          quantity: item.quantity,
        })),
        payment_method: paymentMethod, // Include selected payment method
      },
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        navigate(`/order-confirmation/${result.order._id}`);
        clearCart();
      } else {
        const error = await response.json();
        alert(`Failed to place order: ${error.message}`);
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div>
      <Container fluid className="place-order-section py-4">
        <Row className="g-4">
          <Col lg={6} xs={12}>
            <div className="p-4" style={{ backgroundColor: '#EFEFEF' }}>
              <Card>
                <Card.Body>
                  <h4>Delivery Address</h4>
                  {isEditing ? (
                    <Form>
                      <Form.Group controlId="address" className="mb-3">
                        <Form.Label>Delivery Address</Form.Label>
                        <Form.Control
                          type="text"
                          ref={inputRef} // Attach ref for Google Autocomplete
                          value={newAddress}
                          onChange={(e) => setNewAddress(e.target.value)}
                          placeholder="Enter your address"
                          required
                        />
                      </Form.Group>
                      <Button variant="success" onClick={handleSaveAddress}>
                        Save Address
                      </Button>
                    </Form>
                  ) : (
                    <div>
                      <Card.Text className="text-secondary text-sm">
                        {savedAddress || 'N/A'}
                      </Card.Text>
                      <Button
                        variant="primary"
                        onClick={() => {
                          setNewAddress(savedAddress); // Pre-fill with saved address
                          setIsEditing(true);
                        }}
                      >
                        Edit
                      </Button>
                    </div>
                  )}
                  <hr />
                  <h4>Contact Information</h4>
                  <Form>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>First Name</Form.Label>
                        <Form.Control
                          type="text"
                          value={contactInfo.firstName}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, firstName: e.target.value })
                          }
                          required
                        />
                      </Col>
                      <Col>
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control
                          type="text"
                          value={contactInfo.lastName}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, lastName: e.target.value })
                          }
                          required
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                          type="email"
                          value={contactInfo.email}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, email: e.target.value })
                          }
                          required
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                      <Form.Label>Phone</Form.Label>
<Form.Control
  type="text"
  value={contactInfo.phone}
  placeholder=""
  onChange={(e) => {
    const input = e.target.value.replace(/\D/g, ''); // Remove non-digit characters
    let formattedInput = input;

    if (input.length > 3 && input.length <= 6) {
      formattedInput = `(${input.slice(0, 3)}) ${input.slice(3)}`;
    } else if (input.length > 6) {
      formattedInput = `(${input.slice(0, 3)}) ${input.slice(3, 6)}-${input.slice(6, 10)}`;
    }

    setContactInfo({ ...contactInfo, phone: formattedInput });
  }}
  maxLength={14} // Ensure input doesn't exceed phone number length
  required
/>

                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>Birthdate</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="MM/DD/YYYY"
                          value={contactInfo.birthdate}
                          onChange={handleBirthdateChange}
                          required
                        />
                      </Col>
                    </Row>
                  </Form>
                  <h4>Payment Method</h4>
                  <div className="d-flex flex-wrap">
                    {vendorPaymentMethods.map((method, index) => (
                      <Button
                        key={index}
                        variant={paymentMethod === method.method ? 'primary' : 'outline-primary'}
                        className="m-2"
                        onClick={() => setPaymentMethod(method.method)}
                      >
                        {method.fee > 0
                          ? `${method.method} ($${method.fee.toFixed(2)} Fee)`
                          : method.method}
                      </Button>
                    ))}
                  </div>
                </Card.Body>
              </Card>
            </div>
          </Col>

          {/* Right Container: Order Summary and Place Order */}
          <Col lg={6} xs={12}>
            {/* ... existing code ... */}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default TestPlaceOrder;
