import React, { useState, useEffect } from 'react';
import { Container, ButtonGroup, Button } from 'react-bootstrap';
import ProductRow from './ProductRow'; 

const customCategoryOrder = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Topical', 'Merch'];

const VendorProducts = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [error, setError] = useState(null);

    // Fetch products from backend
    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:4000/api/vendors/${vendorId}/products'); // Adjust URL for specific vendor
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setProducts(data);
            setFilteredProducts(data); // Initialize filtered products with all data
        } catch (err) {
            setError(`Error fetching products: ${err.message}`);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    // Handle category filtering
    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
        setFilteredProducts(
            category === 'All'
                ? products
                : products.filter((product) => product.category === category)
        );
    };

    return (
        <Container className="my-4">
            <h2>Product List</h2>
            {error && <p className="text-danger">{error}</p>}

            {/* Category Filter Buttons */}
            <ButtonGroup className="d-flex overflow-auto mb-3">
                <Button variant="outline-secondary" onClick={() => handleCategoryChange('All')}>
                    All
                </Button>
                {customCategoryOrder.map((category) => (
                    <Button key={category} variant="outline-secondary" onClick={() => handleCategoryChange(category)}>
                        {category}
                    </Button>
                ))}
            </ButtonGroup>

            {/* Product Display */}
            {customCategoryOrder.map((category) => (
                <div key={category} className="my-4">
                    {(selectedCategory === 'All' || selectedCategory === category) && (
                        <>
                            <h4>{category}</h4>
                            {filteredProducts
                                .filter((product) => product.category === category)
                                .map((product) => (
                                    <ProductRow
                                        key={product.productId}
                                        id={product.productId}
                                        image={product.primaryImage || 'https://via.placeholder.com/150'}
                                        brand={product.brand}
                                        productName={product.name}
                                        productAmount={`${product.thcContent}mg THC | ${product.cbdContent}mg CBD`}
                                        price={product.price}
                                        salePrice={product.discountedPrice}
                                    />
                                ))}
                        </>
                    )}
                </div>
            ))}

            {/* No Products Message */}
            {filteredProducts.length === 0 && <p>No products available</p>}
        </Container>
    );
};

export default VendorProducts;
