import React, { useState } from 'react';
import AddressInput from './AddressInput';
import VendorList from './VendorList';

const NearbyVendors = () => {
  const [location, setLocation] = useState(null);

  const handleAddressUpdate = (coordinates) => {
    setLocation(coordinates);
  };

  return (
    <div className="container my-4">
      <h1 className="text-center mb-4">Find Vendors Near You</h1>
      <AddressInput onAddressUpdate={handleAddressUpdate} />
      {location && <VendorList location={location} />}
    </div>
  );
};

export default NearbyVendors;
