import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const AddProductPage = () => {
  // ... [keep all existing state and function declarations the same]

  return (
    <div className="container mt-4">
      <h3>Add Product to Inventory</h3>
      <div className="mb-3">
        <label className="form-label fw-bold fs-5">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          {/* ... [keep existing options] */}
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label fw-bold fs-5">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {products.length > 0 && (
        <div className="mb-3">
          <label className="form-label fw-bold fs-5">Matching Products</label>
          <ul className="list-group">
            {/* ... [keep existing product list markup] */}
          </ul>
        </div>
      )}

      {selectedProduct && (
        <>
          <h3 className="fw-bold">Variations</h3>
          {variations.map((variation, index) => (
            <div
              key={index}
              className="variation-section mb-4 p-4 rounded shadow-sm"
              style={{ backgroundColor: '#4a4a4a', color: 'white' }}
            >
              <h5 className="fw-bold">Variation {index + 1}</h5>

              {/* Image Selection Section */}
              <div className="mb-3">
                <label className="form-label fw-bold">Select Image</label>
                <div className="d-flex overflow-auto" style={{ maxWidth: '100%', gap: '10px' }}>
                  {/* ... [keep existing image selection markup] */}
                </div>
              </div>

              {/* Amount Selection */}
              {category !== 'Gear' && (
                <div className="mb-3">
                  <label className="form-label fw-bold">Amount</label>
                  <select
                    className="form-select"
                    value={variation.amount}
                    onChange={(e) => handleVariationChange(index, 'amount', e.target.value)}
                  >
                    {/* ... [keep existing amount options] */}
                  </select>
                </div>
              )}

              {/* Strain Selection */}
              <div className="mb-3 position-relative">
                <label className="form-label fw-bold">Strain</label>
                <input
                  type="text"
                  className="form-control"
                  value={variation.strain}
                  onChange={(e) => handleVariationChange(index, 'strain', e.target.value)}
                  onFocus={() => {
                    if (variations[index].strain.trim()) {
                      setActiveDropdownIndex(index);
                      setDropdownVisible(true);
                    }
                  }}
                  onBlur={() => {
                    setTimeout(() => {
                      setDropdownVisible(false);
                      setActiveDropdownIndex(null);
                    }, 100);
                  }}
                />
                {/* ... [keep existing dropdown logic] */}
              </div>

              {/* Other Input Fields */}
              <div className="mb-3">
                <label className="form-label fw-bold">Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.price}
                  onChange={(e) => handleVariationChange(index, 'price', e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label className="form-label fw-bold">Sale Price (Optional)</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.salePrice}
                  onChange={(e) => handleVariationChange(index, 'salePrice', e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label className="form-label fw-bold">THC % (Optional)</label>
                <input
                  type="text"
                  className="form-control"
                  value={variation.thcContent}
                  onChange={(e) =>
                    handleVariationChange(
                      index,
                      'thcContent',
                      e.target.value.replace(/[^0-9.]/g, '') + '%'
                    )
                  }
                />
              </div>

              <div className="mb-3">
                <label className="form-label fw-bold">Stock</label>
                <input
                  type="number"
                  className="form-control"
                  value={variation.stock}
                  onChange={(e) => handleVariationChange(index, 'stock', e.target.value)}
                />
              </div>

              <button 
                className="btn btn-danger" 
                onClick={() => handleRemoveVariation(index)}
              >
                Remove Variation
              </button>
            </div>
          ))}

          <div className="d-flex flex-column align-items-start gap-4">
            <button 
              className="btn btn-secondary shadow-sm" 
              onClick={handleAddVariation}
              style={{
                width: '150px',
                height: '150px',
                backgroundColor: '#4a4a4a',
                fontSize: '1.2rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: 'none',
                borderRadius: '8px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                transition: 'transform 0.2s',
              }}
              onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              + Add Variation
            </button>

            <button 
              className="btn btn-primary rounded-pill shadow-sm w-100" 
              onClick={handleAddToInventory}
              style={{
                backgroundColor: '#0056b3',
                padding: '15px 30px',
                fontSize: '1.2rem',
                border: 'none',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                transition: 'transform 0.2s',
                maxWidth: '400px'
              }}
              onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              Add to Inventory
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default AddProductPage;
