import React, { useEffect, useState } from 'react';
import VendorCard from './VendorCard';
import { GeoAltFill } from 'react-bootstrap-icons';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import DeliveryPickupToggle from './DeliveryPickupToggle';

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }
  }, []);

  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const toRadians = (degree) => (degree * Math.PI) / 180;
    const R = 3958.8; // Radius of Earth in miles
    const dLat = toRadians(lat2 - lat1);
    const dLng = toRadians(lng2 - lng1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; // Distance in miles
  };

  const fetchVendors = async () => {
    const address = localStorage.getItem('userAddress');
    if (!address) {
      setError('Please provide a valid address.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Fetch coordinates of user's saved address
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          address
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );
      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location for the given address.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      // Fetch vendors nearby
      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || vendorsData.vendors.length === 0) {
        throw new Error('No vendors found near your location.');
      }

      // Calculate distance for each vendor
      const vendorsWithDistance = vendorsData.vendors.map((vendor) => {
        const vendorLat = vendor.deliveryZone.coordinates[1];
        const vendorLng = vendor.deliveryZone.coordinates[0];
        const milesAway = calculateDistance(lat, lng, vendorLat, vendorLng);
        return { ...vendor, milesAway: milesAway.toFixed(1) }; // Include distance
      });

      setVendors(vendorsWithDistance);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVendors();
  }, []);

  const filteredVendors = vendors
    .map((vendor) => {
      const filteredProducts = vendor.products?.filter((product) => {
        const matchesProductName = product.name.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesBrand = product.brand.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesStrain = product.strain.toLowerCase().includes(searchQuery.trim().toLowerCase());
        return matchesProductName || matchesBrand || matchesStrain;
      });

      if (filteredProducts?.length > 0) {
        return { ...vendor, products: [...filteredProducts] }; // Always return a new copy of products
      }
      return null; // Exclude vendors with no matching products
    })
    .filter((vendor) => vendor !== null);

  if (!parsedAddress.street && !parsedAddress.city && !parsedAddress.stateAbbreviation) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container-fluid p-0">
      {/* Toolbar Section */}
      <div
  className="toolbar-container shadow-sm py-2 px-3"
  style={{
    position: 'sticky',
    top: 0,
    zIndex: 1030,
    backgroundColor: '#FFF176',
  }}
>
  <div className="d-flex align-items-center justify-content-between">
    {/* Reusable SearchBar Component */}
    <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
    <DeliveryPickupToggle />


  </div>
</div>


      {/* Vendor Cards Section */}
      <div className="vendor-list">
        {loading ? (
          <p>Loading vendors...</p>
        ) : error ? (
          <p className="text-danger">{error}</p>
        ) : filteredVendors.length ? (
          filteredVendors.map((vendor) => (
            <div key={vendor._id} className="vendor-card-wrapper">
              <VendorCard vendor={vendor} />
            </div>
          ))
        ) : (
          <p>No vendors or products match your search.</p>
        )}
      </div>
    </div>
  );
};

export default NearbyVendorsPage;
