import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState(null); // User state
    const [loading, setLoading] = useState(true); // Loading state

    // Function to log in the user
    const login = async () => {
        try {
            // Check authentication status after login
            const response = await axios.get('http://localhost:4000/api/auth/validate', {
                withCredentials: true, // Include cookies for JWT validation
            });

            if (response.data?.success) {
                setAuth(response.data.vendor); // Update user state with vendor data
            } else {
                throw new Error('Login failed. Unable to validate user.');
            }
        } catch (error) {
            console.error('Login validation failed:', error);
            setAuth(null); // Ensure no stale auth state remains
        }
    };

    // Function to log out the user
    const logout = async () => {
        try {
            await axios.post('http://localhost:4000/api/auth/logout', {}, {
                withCredentials: true, // Include cookies with request
            });

            setAuth(null); // Clear user state
            window.location.href = '/login'; // Redirect to login page
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    // Function to check authentication status
    const checkAuthStatus = async () => {
        try {
            const response = await axios.get('http://localhost:4000/api/auth/validate', {
                withCredentials: true, // Include cookies with request
            });

            if (response.data?.success) {
                setAuth(response.data.vendor); // Update user state
            } else {
                setAuth(null); // Clear state if the user is not authenticated
            }
        } catch (error) {
            console.error('User is not authenticated:', error);
            setAuth(null);
        } finally {
            setLoading(false); // Stop loading after the check
        }
    };

    // Automatically check authentication status on mount
    useEffect(() => {
        checkAuthStatus();
    }, []);

    return (
        <AuthContext.Provider value={{ auth, login, logout, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

// Custom hook for accessing the AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
};
