import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card, Alert } from 'react-bootstrap';
import { Send, MapPin, Phone, Mail } from 'lucide-react';
import emailjs from '@emailjs/browser';

// Initialize EmailJS with your public key
emailjs.init(process.env.REACT_APP_EMAILJS_PUBLIC_KEY);

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setShowSuccess(false);
    setShowError(false);

    try {
      const templateParams = {
        from_name: formData.name,
        from_email: formData.email,
        subject: formData.subject,
        message: formData.message,
        to_email: 'Lbkenji1@yahoo.com',
      };

      await emailjs.send(
        process.env.REACT_APP_EMAILJS_SERVICE_ID,
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
        templateParams
      );

      setShowSuccess(true);
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: ''
      });
    } catch (error) {
      console.error('Error sending message:', error);
      setShowError(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh', paddingTop: '2rem', paddingBottom: '4rem' }}>
      <Container>
        <Row className="justify-content-center">
          <Col md={10} lg={8}>
            <h1 className="text-center mb-4" style={{ fontWeight: '700', color: '#2c3e50' }}>
              Contact Us
            </h1>
            <p className="text-center mb-5" style={{ color: '#6c757d' }}>
              Have questions? We'd love to hear from you. Send us a message and we'll respond as soon as possible.
            </p>

            {/* Contact Information Cards */}
            <Row className="mb-5 g-4">
              <Col md={4}>
                <Card className="h-100 border-0 shadow-sm">
                  <Card.Body className="text-center p-4">
                    <div className="mb-3">
                      <MapPin size={24} className="text-primary" />
                    </div>
                    <h5 className="fw-bold mb-2">Address</h5>
                    <p className="text-muted mb-0">Los Angeles, CA</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4}>
                <Card className="h-100 border-0 shadow-sm">
                  <Card.Body className="text-center p-4">
                    <div className="mb-3">
                      <Phone size={24} className="text-primary" />
                    </div>
                    <h5 className="fw-bold mb-2">Phone</h5>
                    <p className="text-muted mb-0">(555) 123-4567</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4}>
                <Card className="h-100 border-0 shadow-sm">
                  <Card.Body className="text-center p-4">
                    <div className="mb-3">
                      <Mail size={24} className="text-primary" />
                    </div>
                    <h5 className="fw-bold mb-2">Email</h5>
                    <p className="text-muted mb-0">Lbkenji1@yahoo.com</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Contact Form */}
            <Card className="border-0 shadow-sm">
              <Card.Body className="p-4 p-md-5">
                {showSuccess && (
                  <Alert variant="success" onClose={() => setShowSuccess(false)} dismissible>
                    Your message has been sent successfully! We'll get back to you soon.
                  </Alert>
                )}

                {showError && (
                  <Alert variant="danger" onClose={() => setShowError(false)} dismissible>
                    There was an error sending your message. Please try again later.
                  </Alert>
                )}

                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group className="mb-4">
                        <Form.Label style={{ fontWeight: '600' }}>Your Name</Form.Label>
                        <Form.Control
                          type="text"
                          name="name"
                          value={formData.name}
                          onChange={handleInputChange}
                          required
                          style={{ padding: '0.75rem', borderRadius: '0.5rem' }}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group className="mb-4">
                        <Form.Label style={{ fontWeight: '600' }}>Email Address</Form.Label>
                        <Form.Control
                          type="email"
                          name="email"
                          value={formData.email}
                          onChange={handleInputChange}
                          required
                          style={{ padding: '0.75rem', borderRadius: '0.5rem' }}
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Form.Group className="mb-4">
                    <Form.Label style={{ fontWeight: '600' }}>Subject</Form.Label>
                    <Form.Control
                      type="text"
                      name="subject"
                      value={formData.subject}
                      onChange={handleInputChange}
                      required
                      style={{ padding: '0.75rem', borderRadius: '0.5rem' }}
                    />
                  </Form.Group>

                  <Form.Group className="mb-4">
                    <Form.Label style={{ fontWeight: '600' }}>Message</Form.Label>
                    <Form.Control
                      as="textarea"
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      required
                      rows={5}
                      style={{ padding: '0.75rem', borderRadius: '0.5rem', resize: 'vertical' }}
                    />
                  </Form.Group>

                  <div className="text-center">
                    <Button
                      type="submit"
                      variant="primary"
                      size="lg"
                      disabled={isSubmitting}
                      className="px-5 py-3"
                      style={{
                        borderRadius: '0.5rem',
                        fontWeight: '600',
                        backgroundColor: '#007bff',
                        border: 'none'
                      }}
                    >
                      <Send size={20} className="me-2" />
                      {isSubmitting ? 'Sending...' : 'Send Message'}
                    </Button>
                  </div>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ContactPage;
