import React, { useState } from 'react';
import './MenuSearchBar.css';
import { FaSearch } from 'react-icons/fa'; // FontAwesome icon for the search

const MenuSearchBar = ({ onSearch }) => {
  const [query, setQuery] = useState('');

  // Handle input change and trigger the search function passed from the parent component
  const handleSearch = (e) => {
    setQuery(e.target.value);
    onSearch(e.target.value); // Pass the search query to the parent component
  };

  return (
    <div className="row mb-3">
      <div className="d-flex col-12 justify-content-center">
        <div className="position-relative w-100">
          {/* Input field with circle ends */}
          <input
            type="text"
            className="form-control rounded-pill ps-5" // Add padding-left to make space for the icon
            placeholder="Search menu..."
            value={query}
            onChange={handleSearch}
            aria-label="Search"
          />
          {/* Search icon placed inside the input field */}
          <FaSearch 
            className="position-absolute search-icon text-muted" 
            style={{ fontSize: '0.8rem' }} // Smaller size 
            />
        </div>
      </div>

    </div>
  );
};

export default MenuSearchBar;
