import React, { useEffect, useState } from 'react';
import VendorCard from './VendorCard';
import { Form, InputGroup } from 'react-bootstrap';
import { GeoAltFill } from 'react-bootstrap-icons';
import { useNavigate } from 'react-router-dom';
import PickupMap from './PickupMap';
import './NearbyPickupPage.css';

const NearbyPickupPage = () => {
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [cardPosition, setCardPosition] = useState('middle'); // 'middle' or 'full'
  const [startY, setStartY] = useState(0);
  const [currentY, setCurrentY] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const calculateDistanceInMiles = (lat1, lng1, lat2, lng2) => {
    const toRadians = (degree) => (degree * Math.PI) / 180;
    const R = 3963; // Earth's radius in miles
    const dLat = toRadians(lat2 - lat1);
    const dLng = toRadians(lng2 - lng1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) * Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const fetchVendors = async () => {
    const address = localStorage.getItem('userAddress');
    if (!address) {
      setError('Please provide a valid address.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          address
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );
      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location for the given address.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || vendorsData.vendors.length === 0) {
        throw new Error('No vendors found near your location.');
      }

      const userCoordinates = { lat, lng };
      const enrichedVendors = vendorsData.vendors.map((vendor) => {
        if (vendor.deliveryZone && vendor.deliveryZone.coordinates?.length === 2) {
          const [vendorLng, vendorLat] = vendor.deliveryZone.coordinates;
          const distanceInMiles = calculateDistanceInMiles(
            userCoordinates.lat,
            userCoordinates.lng,
            vendorLat,
            vendorLng
          );
          return { ...vendor, milesAway: distanceInMiles.toFixed(1) };
        } else {
          console.warn(`Vendor ${vendor.dispensaryName} does not have valid delivery coordinates.`);
          return { ...vendor, milesAway: 'N/A' };
        }
      });

      setVendors(enrichedVendors);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }
  }, []);

  useEffect(() => {
    fetchVendors();
  }, []);

  const handleDragStart = (e) => {
    setIsDragging(true);
    setStartY(e.touches ? e.touches[0].clientY : e.clientY);
  };

  const handleDragMove = (e) => {
    if (!isDragging) return;
    const currentY = e.touches ? e.touches[0].clientY : e.clientY;
    setCurrentY(currentY);
  };

  const handleDragEnd = () => {
    setIsDragging(false);
    if (currentY > startY + 100) {
      setCardPosition('middle');
    } else if (currentY < startY - 100) {
      setCardPosition('full');
    }
  };

  return (
    <div className="container-fluid p-0">
      {/* Toolbar Section */}
      <div className="toolbar-container shadow-sm py-3 px-3 border">
        {/* Toolbar Content */}
      </div>

      {/* Map Section */}
      <PickupMap
        dispensaries={vendors}
        onMarkerClick={(vendor) => {
          setSelectedVendor(vendor);
          setCardPosition('middle');
        }}
      />

      {/* Sliding Vendor Card */}
      {selectedVendor && (
        <div
          className={`vendor-card-container ${cardPosition}`}
          style={{
            transform: `translateY(${cardPosition === 'full' ? '0' : '50%'})`,
          }}
        >
          <div
            className="drag-handle"
            onMouseDown={handleDragStart}
            onTouchStart={handleDragStart}
            onMouseMove={handleDragMove}
            onTouchMove={handleDragMove}
            onMouseUp={handleDragEnd}
            onTouchEnd={handleDragEnd}
          ></div>
          <VendorCard key={selectedVendor._id} vendor={selectedVendor} />
        </div>
      )}
    </div>
  );
};

export default NearbyPickupPage;
