import React, { createContext, useState, useEffect } from "react";

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Fetch vendor data
  const fetchVendorData = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/api/vendors/${id}`);
      if (!response.ok) throw new Error("Failed to fetch vendor data.");
      const data = await response.json();
      setVendor(data.data);
    } catch (err) {
      console.error("Error fetching vendor data:", err.message);
      throw err;
    }
  };

  // Fetch products data
  const fetchProductsData = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5001/api/products/vendor/${id}/products`
      );
      if (!response.ok) throw new Error("Failed to fetch products data.");
      const data = await response.json();
      setProducts(data.data.products);
    } catch (err) {
      console.error("Error fetching products data:", err.message);
      throw err;
    }
  };

  // Unified data fetching
  const fetchAllData = async () => {
    try {
      setError(null);
      setIsLoading(true);
      await Promise.all([fetchVendorData(vendorId), fetchProductsData(vendorId)]);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (vendorId) {
      fetchAllData();
    }
  }, [vendorId]);

  return (
    <VendorContext.Provider value={{ vendor, products, error, isLoading }}>
      {children}
    </VendorContext.Provider>
  );
};
