import React from 'react';
import { FaRegThumbsUp } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const VendorRow = ({ vendor }) => {
  // Define styles for status circles
  const statusCircleStyle = (status) => ({
    display: 'inline-block',
    width: '10px',
    height: '10px',
    borderRadius: '50%',
    backgroundColor: status === 'Open' ? 'green' : 'red',
    marginRight: '5px'
  });

  return (
    <div className="col-12 mb-3">
      <div className="card rounded-pill mx-3">
        <div className="card-body d-flex align-items-center ms-1">
          {/* Circular Logo Image */}
          <img
            src={vendor.logo}
            alt={`${vendor.name} logo`}
            style={{
              width: '50px',
              height: '50px',
              marginRight: '15px',
              borderRadius: '50%',
              objectFit: 'cover'
            }}
          />

          {/* Vendor Info Column */}
          <div className="d-flex flex-column flex-grow-1">
            {/* Vendor Name */}
            <h5 className="mb-1">
                <Link to={`/vendor/${vendor.id}`} className="text-dark text-decoration-none">{vendor.name}</Link></h5>

            {/* Vendor Status, Rating, and Order Minimum */}
            <div className="d-flex align-items-center">
              {/* Availability (Open/Closed) with Circle */}
              <div style={statusCircleStyle(vendor.status)}></div>
              <small className="text-muted me-3">
                {vendor.status === 'Open' ? 'Available' : 'Closed'}
              </small>

              {/* Thumbs Up Icon and Rating */}
              <FaRegThumbsUp className="me-1 text-muted" />
              <small className="text-muted me-3">{vendor.rating}%</small>

            </div>
          </div>

          <Link to={`/vendor/${vendor.id}`} className="btn btn-dark rounded-pill ms-auto" style={{ width: '150px' }}>
            Menu <span style={{ fontSize: '11px', lineHeight: '1.2' }}>({vendor.menuItems})</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default VendorRow;
