import React, { useState } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

function MapComponent({ onLocationChange }) {
  // Initialize the marker location within the MapComponent
  const [location, setLocation] = useState({
    lat: 34.0, // Preset latitude
    lng: -118.3, // Preset longitude
  });

  const handleMarkerDragEnd = (event) => {
    const newLocation = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setLocation(newLocation); // Update marker location
    onLocationChange(newLocation); // Notify the parent component of the new location
  };

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={location} // Center the map on the marker
      zoom={10}
    >
      <Marker
        position={location}
        draggable
        onDragEnd={handleMarkerDragEnd}
      />
    </GoogleMap>
  );
}

export default MapComponent;
