import React, { useState, useEffect, useContext } from 'react';
import { Badge } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const RecentOrders = () => {
  const { auth } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [expandedOrderId, setExpandedOrderId] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      if (!auth?.vendorId) {
        console.error('Vendor ID is not defined in AuthContext');
        return;
      }

      try {
        const response = await axios.get(`/api/orders/vendor/${auth.vendorId}`, {
          withCredentials: true,
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching vendor orders:', error);
      }
    };

    fetchOrders();
  }, [auth]);

  const handleRowClick = (orderId) => {
    setExpandedOrderId(expandedOrderId === orderId ? null : orderId);
  };

  const handleStatusChange = async (orderId, status) => {
    try {
      await axios.put(`/api/orders/${orderId}/status`, 
        { status }, 
        { headers: { Authorization: `Bearer ${auth.token}` }}
      );
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, status } : order
        )
      );
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  const formatBirthdate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      // You could add a toast notification here
    } catch (error) {
      console.error('Failed to copy:', error);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-6">Recent Orders</h2>
      <div className="space-y-4">
        {orders.map((order) => (
          <div 
            key={order._id}
            className="border rounded-lg overflow-hidden hover:border-gray-400 transition-colors"
          >
            {/* Order Header - Always Visible */}
            <div
              onClick={() => handleRowClick(order._id)}
              className="flex items-center justify-between p-4 cursor-pointer bg-gray-50 hover:bg-gray-100"
            >
              <div className="flex items-center space-x-4">
                <div>
                  <div className="text-sm text-gray-500">
                    {new Date(order.createdAt).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                  <div className="font-semibold">
                    {order.contactInfo?.firstName} {order.contactInfo?.lastName}
                  </div>
                </div>
                <div className="flex gap-2">
                  <Badge bg={
                    order.status === 'COMPLETED' ? 'success' :
                    order.status === 'CANCELLED' ? 'danger' : 'warning'
                  }>
                    {order.status.replace('_', ' ')}
                  </Badge>
                  <Badge bg="info">{order.orderType}</Badge>
                </div>
              </div>
              <div className="text-right">
                <div className="font-bold text-lg">${order.total?.toFixed(2)}</div>
                <div className="text-sm text-gray-500">{order.items.length} items</div>
              </div>
            </div>

            {/* Expanded Order Details */}
            {expandedOrderId === order._id && (
              <div className="border-t p-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Left Column - Customer Details */}
                  <div className="space-y-4">
                    <div>
                      <h3 className="font-semibold mb-2">Customer Information</h3>
                      <div className="bg-gray-50 p-3 rounded">
                        <div className="mb-2">
                          <div className="font-medium">{order.contactInfo?.firstName} {order.contactInfo?.lastName}</div>
                          <div className="text-sm text-gray-600">DOB: {formatBirthdate(order.customer?.birthdate)}</div>
                          <div className="text-sm text-gray-600">{order.contactInfo?.email}</div>
                        </div>
                        <div className="flex items-center justify-between">
                          <div className="text-sm text-gray-600">{order.contactInfo?.phone}</div>
                          <button
                            onClick={() => copyToClipboard(order.contactInfo?.phone)}
                            className="text-blue-600 hover:text-blue-800 text-sm px-2 py-1"
                          >
                            Copy
                          </button>
                        </div>
                      </div>
                    </div>

                    {order.orderType === 'Delivery' && order.deliveryAddress && (
                      <div>
                        <h3 className="font-semibold mb-2">Delivery Address</h3>
                        <div className="bg-gray-50 p-3 rounded">
                          <div className="flex items-center justify-between">
                            <div className="text-sm">
                              {order.deliveryAddress.street}<br />
                              {order.deliveryAddress.city}, {order.deliveryAddress.state} {order.deliveryAddress.zip}
                            </div>
                            <button
                              onClick={() => copyToClipboard(
                                `${order.deliveryAddress.street}, ${order.deliveryAddress.city}, ${order.deliveryAddress.state} ${order.deliveryAddress.zip}`
                              )}
                              className="text-blue-600 hover:text-blue-800 text-sm px-2 py-1"
                            >
                              Copy
                            </button>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Action Buttons */}
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleStatusChange(order._id, 'CANCELLED')}
                        className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 flex-1"
                      >
                        Cancel Order
                      </button>
                      <button
                        onClick={() => handleStatusChange(order._id, 'COMPLETED')}
                        className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 flex-1"
                      >
                        Mark Completed
                      </button>
                    </div>
                  </div>

                  {/* Right Column - Order Items */}
                  <div>
                    <h3 className="font-semibold mb-2">Order Items</h3>
                    <div className="space-y-3">
                      {order.items.map((item, index) => (
                        <div key={index} className="flex items-center gap-3 bg-gray-50 p-2 rounded">
                          {item.image && (
                            <img 
                              src={item.image} 
                              alt={item.name}
                              className="w-16 h-16 object-cover rounded"
                            />
                          )}
                          <div className="flex-1 min-w-0">
                            <div className="font-medium truncate">{item.name}</div>
                            <div className="text-sm text-gray-600">
                              {item.salePrice ? (
                                <span>
                                  <span className="line-through mr-2">${item.price?.toFixed(2)}</span>
                                  <span className="text-red-600">${item.salePrice?.toFixed(2)}</span>
                                </span>
                              ) : (
                                <span>${item.price?.toFixed(2)}</span>
                              )} x {item.quantity}
                            </div>
                            {item.category && (
                              <div className="text-xs text-gray-500">{item.category}</div>
                            )}
                          </div>
                          <div className="font-medium whitespace-nowrap">
                            ${((item.salePrice || item.price) * item.quantity).toFixed(2)}
                          </div>
                        </div>
                      ))}
                    </div>

                    {/* Order Summary */}
                    <div className="mt-4 bg-gray-50 p-3 rounded">
                      <div className="flex justify-between text-sm mb-2">
                        <span>Subtotal</span>
                        <span>${order.items.reduce((sum, item) => 
                          sum + ((item.salePrice || item.price) * item.quantity), 0).toFixed(2)}</span>
                      </div>
                      <div className="flex justify-between font-bold">
                        <span>Total</span>
                        <span>${order.total?.toFixed(2)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecentOrders;
