import React, { useState, useEffect } from 'react';
import NearbyVendorsPage from './NearbyVendorsPage';
import NearbyPickupPage from './NearbyPickupPage';
import SearchBar from './SearchBar';
import DeliveryPickupToggle from './DeliveryPickupToggle';
import './DeliveryPickupWrapper.css';

const DeliveryPickupWrapper = ({ onNavigate }) => {
  const [selectedOption, setSelectedOption] = useState('Delivery'); // Default to 'delivery'
  const [searchQuery, setSearchQuery] = useState('');
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true); // Trigger animation after mounting
  }, []);

  return (
    <div
      className={`delivery-pickup-wrapper ${animate ? 'slide-in' : ''}`}
      style={{
        minHeight: '100vh',
        margin: '0',
        padding: '0',
      }}
    >
      {/* Toolbar Section */}
      <div className="toolbar-container">
        <div className="d-flex align-items-center justify-content-between px-3 py-3">
          <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
          <DeliveryPickupToggle selectedOption={selectedOption} setSelectedOption={setSelectedOption} />
        </div>
      </div>

      {/* Content Section */}
      <div className="content-section">
        {selectedOption === 'delivery' ? (
          <NearbyVendorsPage searchQuery={searchQuery} />
        ) : (
          <NearbyPickupPage searchQuery={searchQuery} />
        )}
      </div>
    </div>
  );
};

export default DeliveryPickupWrapper;
