import React, { useState } from 'react';
import { useLoadScript } from '@react-google-maps/api';
import MapComponent from './MapComponent';

const libraries = ['places']; // Define this outside the component

function UpdateDeliveryZoneComponent() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyBKb82KnQEbd1OGJxY08DbvsmRcz33h48w',
    libraries,
  });

  const [location, setLocation] = useState({
    lat: 34.0522, // Default latitude
    lng: -118.2437, // Default longitude
  });

  const handleUpdateDeliveryZone = async () => {
    try {
      const response = await fetch('/api/vendors/update-delivery-zone', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ deliveryZone: location }),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Delivery zone updated successfully!');
      } else {
        alert(`Failed to update delivery zone: ${result.message}`);
      }
    } catch (error) {
      console.error('Error updating delivery zone:', error);
      alert('An error occurred while updating the delivery zone.');
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading...</div>;

  return (
    <div>
      <h2>Set Delivery Zone</h2>
      <MapComponent location={location} setLocation={setLocation} />
      <div style={{ marginTop: '20px' }}>
        <label>
          Latitude:
          <input
            type="text"
            value={location.lat}
            onChange={(e) =>
              setLocation({ ...location, lat: parseFloat(e.target.value) })
            }
            style={{ marginLeft: '10px', marginRight: '20px' }}
          />
        </label>
        <label>
          Longitude:
          <input
            type="text"
            value={location.lng}
            onChange={(e) =>
              setLocation({ ...location, lng: parseFloat(e.target.value) })
            }
            style={{ marginLeft: '10px', marginRight: '20px' }}
          />
        </label>
        <button onClick={handleUpdateDeliveryZone} style={{ marginLeft: '10px' }}>
          Update Delivery Zone
        </button>
      </div>
    </div>
  );
}

export default UpdateDeliveryZoneComponent;
