import React, { useState, useContext } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const ProductDetails = ({ show, handleClose, product }) => {
  const { addToCart, incrementItem, decrementItem, cartItems } = useContext(CartContext);
  const [localQuantity, setLocalQuantity] = useState(1);

  if (!product) return null;

  // Find the current quantity of the product in the cart
  const cartItem = cartItems.find((item) => item._id === product._id);
  const currentQuantity = cartItem ? cartItem.quantity : 0;

  // Handle Increment
  const handleIncrement = () => {
    setLocalQuantity((prev) => prev + 1);
  };

  // Handle Decrement
  const handleDecrement = () => {
    setLocalQuantity((prev) => (prev > 1 ? prev - 1 : 1));
  };

  // Add to Cart Handler
  const handleAddToCart = () => {
    addToCart({
      ...product,
      salePrice: product.variation.salePrice,
      quantity: localQuantity,
      vendorName: product.vendorName,
      logoUrl: product.logoUrl,
    });
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>
          <strong>{product.brand}</strong> | {product.category}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="text-center">
          <img
            src={product.variation?.image || '/placeholder-image.png'}
            alt={product.name || 'Product Image'}
            className="img-fluid mb-4"
            style={{ maxHeight: '200px', objectFit: 'contain' }}
          />
        </div>

        <div className="d-flex align-items-center mb-3">
          <h5 className="fw-bold mb-0 me-3">{product.name}</h5>
          {product.variation?.amount && (
            <span
              className="badge bg-light text-dark"
              style={{
                fontSize: '0.9rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.3rem 0.6rem',
              }}
            >
              {product.variation.amount}
            </span>
          )}
        </div>
        <p className="text-muted">{product.description || ''}</p>

        <div className="mb-2 d-flex align-items-center">
          <strong>{product.variation?.strain || 'N/A'}</strong>
          {product.variation?.thcContent && (
            <span
              className="badge border border-secondary text-secondary ms-3"
              style={{
                fontSize: '0.9rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.3rem 0.6rem',
              }}
            >
              {product.variation.thcContent}% THC
            </span>
          )}
        </div>
      </Modal.Body>

      {/* Footer */}
      <Modal.Footer className="d-flex justify-content-between align-items-center">
        {/* Price Badge */}
        <div>
          {product.variation?.salePrice ? (
            <>
              <span
                className="badge bg-light"
                style={{
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  color: '#FF0080',
                  borderRadius: '0.5rem',
                  padding: '0.6rem 1.2rem',
                }}
              >
                ${product.variation?.salePrice ? product.variation.salePrice.toFixed(2) : '0.00'}
              </span>
              <span
                style={{
                  textDecoration: 'line-through',
                  color: 'gray',
                  fontSize: '1rem',
                  marginLeft: '0.5rem',
                }}
              >
                ${product.variation?.price ? product.variation.price.toFixed(2) : '0.00'}
              </span>
            </>
          ) : (
            <span
              className="badge bg-light text-dark"
              style={{
                fontSize: '1.2rem',
                fontWeight: 'bold',
                borderRadius: '0.5rem',
                padding: '0.6rem 1.2rem',
              }}
            >
              ${product.variation?.price?.toFixed(2) || 'N/A'}
            </span>
          )}
        </div>

        {/* Quantity Controls */}
        <div className="d-flex align-items-center">
          <Button
            variant="outline-secondary"
            size="sm"
            style={{ fontSize: '1.2rem', padding: '0.3rem 0.6rem' }}
            onClick={handleDecrement}
          >
            âˆ’
          </Button>
          <span
            style={{
              fontSize: '1.2rem',
              fontWeight: 'bold',
              margin: '0 1rem',
              minWidth: '2rem',
              textAlign: 'center',
            }}
          >
            {localQuantity}
          </span>
          <Button
            variant="outline-secondary"
            size="sm"
            style={{ fontSize: '1.2rem', padding: '0.3rem 0.6rem' }}
            onClick={handleIncrement}
          >
            +
          </Button>
        </div>

        {/* Add to Cart Button */}
        <div>
          <Button
            variant="primary"
            className="rounded-circle d-flex align-items-center justify-content-center"
            style={{
              width: '3rem',
              height: '3rem',
              fontSize: '1.5rem',
              fontWeight: 'bold',
            }}
            onClick={(e) => {
                addToCart({
                  ...product,
                  price: price,
                  salePrice: salePrice,
                  vendorName,
                  logoUrl,
                });
              }}
          >
            +
          </Button>
        </div>
      </Modal.Footer>
    </Modal>
  );
};

export default ProductDetails;
