import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import AddressInputCard from './AddressInputCard';

const AddressInput = ({ onAddressUpdate }) => {
  const [show, setShow] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAddressUpdate = (location) => {
    setSelectedAddress(location.address); // Update the selected address
    onAddressUpdate(location); // Pass location to parent
  };

  return (
    <>
      {/* Main Display: Trigger Modal */}
      <div className="address-bar d-flex align-items-center">
        <i className="bi bi-geo-alt-fill me-2"></i>
        <span
          className="address-link"
          onClick={handleShow}
          style={{ textDecoration: 'underline', cursor: 'pointer' }}
        >
          {selectedAddress ? `Deliver to ${selectedAddress}` : 'Enter your address'}
        </span>
      </div>

      {/* Modal for AddressInputCard */}
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Address Input</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <AddressInputCard onAddressUpdate={handleAddressUpdate} onClose={handleClose} />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AddressInput;
