import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import VendorInfoCard from './'

const DashNavBar = ({ vendorData }) => {
  const [showSidebar, setShowSidebar] = useState(false);
  const navigate = useNavigate();

  const handleClose = () => {
    setShowSidebar(false);
  };

  const handleLogoClick = () => {
    if (vendorData?._id) {
      navigate(`/vendor/${vendorData._id}/menu`);
    }
  };

  return (
    <>
      {/* Main Navbar */}
      <nav className="fixed top-0 left-0 right-0 h-16 bg-zinc-900 shadow-lg z-30">
        <div className="h-full px-4 mx-auto flex items-center justify-between">
          {/* Left - Company Logo */}
          <div className="flex items-center">
            <img
              src={require('../assets/images/miniLogo.jpg')}
              alt="Mini Logo"
              className="h-9 w-auto rounded-md cursor-pointer hover:opacity-90 transition-opacity"
              onClick={handleLogoClick}
            />
          </div>

          {/* Right - Greeting and Profile */}
          <div className="flex items-center space-x-4">
            <span className="text-white text-sm">
              Hi, <span className="font-medium">{vendorData?.firstName || 'Vendor'}</span>
            </span>
            <button
              onClick={() => setShowSidebar(true)}
              className="relative w-10 h-10 rounded-full overflow-hidden hover:ring-2 hover:ring-blue-400 transition-all focus:outline-none"
            >
              <img
                src={vendorData?.logoUrl || ''}
                alt="Vendor Logo"
                className="w-full h-full object-cover"
              />
            </button>
          </div>
        </div>
      </nav>

      {/* Overlay */}
      {showSidebar && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 transition-opacity"
          onClick={handleClose}
        />
      )}

      {/* Sidebar */}
      <div
        className={`fixed top-0 right-0 w-80 h-full bg-white shadow-lg z-50 transform transition-transform duration-300 ease-in-out ${
          showSidebar ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        <div className="h-full overflow-y-auto">
          {/* Header */}
          <div className="px-6 py-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold text-gray-800">Profile</h2>
              <button
                onClick={handleClose}
                className="p-2 hover:bg-gray-100 rounded-full transition-colors focus:outline-none"
              >
                <svg
                  className="w-5 h-5 text-gray-500"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>
          </div>

          {/* Content */}
          <div className="p-6 space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <VendorInfoCard className="p-4" />
            </div>

            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <DispensaryInfoCard className="p-4" />
            </div>

            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <VendorLogoCard className="p-4" />
            </div>

            <div className="mt-8 flex justify-center">
              <DeactivateAccount className="w-full max-w-xs px-4 py-2 bg-red-50 text-red-600 rounded-lg hover:bg-red-100 transition-colors text-sm font-medium" />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default DashNavBar;
