import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import { Button, Row, Col, Form, Card, Spinner, Alert } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const {
    calculateTotal,
    cartItems,
    incrementItem,
    decrementItem,
    removeFromCart,
    getDispensaryName,
  } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    day: '',
    month: '',
    year: '',
  });

  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const [loading, setLoading] = useState(false); // For the loading spinner
  const [errorMessage, setErrorMessage] = useState(''); // For error popup

  const navigate = useNavigate(); // To redirect user to confirmation page

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  const dispensaryName = getDispensaryName();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress({ ...deliveryAddress, [name]: value });
  };

  const handlePlaceOrder = async () => {
    setLoading(true); // Start loading
    setErrorMessage(''); // Clear any previous error messages

    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      setLoading(false);
      return;
    }
    if (!deliveryAddress.street || !deliveryAddress.city || !deliveryAddress.state || !deliveryAddress.zip) {
      alert('Please fill out all delivery address fields.');
      setLoading(false);
      return;
    }

    const birthdate = `${contactInfo.year}-${contactInfo.month.toString().padStart(2, '0')}-${contactInfo.day.toString().padStart(2, '0')}`;

    const orderDetails = {
      type: 'DELIVERY',
      order_source: 'ECOMMERCE',
      order_status: 'AWAITING_PROCESSING',
      delivery_address: deliveryAddress,
      items: cartItems.map((item) => ({
        size_id: item.productId,
        quantity: item.quantity,
      })),
    };

    const payload = {
      phone: contactInfo.phone,
      firstName: contactInfo.firstName,
      lastName: contactInfo.lastName,
      email: contactInfo.email,
      birthday: birthdate,
      vendorId: cartItems[0]?.vendorId, // Assuming all items belong to the same vendor
      orderDetails,
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        navigate(`/order-confirmation/${result.order._id}`); // Redirect to confirmation page
      } else {
        const error = await response.json();
        setErrorMessage(error.message || 'Failed to place order. Please try again.');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      setErrorMessage('An error occurred. Please try again later.');
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="place-order-section p-4 bg-light">
      {loading && (
        <div className="loading-overlay">
          <Spinner animation="border" variant="primary" />
          <p>Placing your order...</p>
        </div>
      )}
      {errorMessage && (
        <Alert variant="danger" className="mt-3">
          {errorMessage}
        </Alert>
      )}
      {/* Delivery Address Section */}
      <div className="mb-4">
        <h4>Delivery Address</h4>
        <Form>
          <Form.Group controlId="street" className="mb-3">
            <Form.Label>Street Address</Form.Label>
            <Form.Control
              type="text"
              name="street"
              value={deliveryAddress.street}
              onChange={handleAddressChange}
              required
            />
          </Form.Group>
          <Row className="mb-3">
            <Col>
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                name="city"
                value={deliveryAddress.city}
                onChange={handleAddressChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>State</Form.Label>
              <Form.Control
                as="select"
                name="state"
                value={deliveryAddress.state}
                onChange={handleAddressChange}
                required
              >
                <option value="">Select</option>
                {['CA', 'CO', 'NY', 'FL', 'NV', 'OR', 'NJ'].map((state) => (
                  <option key={state} value={state}>{state}</option>
                ))}
              </Form.Control>
            </Col>
            <Col>
              <Form.Label>ZIP Code</Form.Label>
              <Form.Control
                type="text"
                name="zip"
                value={deliveryAddress.zip}
                onChange={handleAddressChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Contact Information Section */}
      <div className="mb-4">
        <h4>Contact Information</h4>
        <Form>
          <Row className="mb-3">
            <Col>
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={contactInfo.firstName}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={contactInfo.lastName}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Birthdate</Form.Label>
              <Row>
                <Col>
                  <Form.Control
                    as="select"
                    name="year"
                    value={contactInfo.year}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Year</option>
                    {[...Array(100).keys()].map((i) => {
                      const year = new Date().getFullYear() - i;
                      return <option key={year} value={year}>{year}</option>;
                    })}
                  </Form.Control>
                </Col>
                <Col>
                  <Form.Control
                    as="select"
                    name="month"
                    value={contactInfo.month}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Month</option>
                    {Array.from({ length: 12 }, (_, i) => i + 1).map((month) => (
                      <option key={month} value={month}>{month}</option>
                    ))}
                  </Form.Control>
                </Col>
                <Col>
                  <Form.Control
                    as="select"
                    name="day"
                    value={contactInfo.day}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Day</option>
                    {Array.from({ length: 31 }, (_, i) => i + 1).map((day) => (
                      <option key={day} value={day}>{day}</option>
                    ))}
                  </Form.Control>
                </Col>
              </Row>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={contactInfo.email}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                name="phone"
                value={contactInfo.phone}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Place Order Button */}
      <Button variant="primary" className="w-100 mb-3" onClick={handlePlaceOrder} disabled={loading}>
        {loading ? 'Placing Order...' : 'Place Order'}
      </Button>
    </div>
  );
};

export default TestPlaceOrder;
