import React, { useState, useEffect } from 'react';
import { Offcanvas, ListGroup, Button, Form, InputGroup, FormControl, Badge, Modal } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import AddProductPage from './AddProductPage'; // Import AddProductPage

const InventoryComponent = () => {
  const { auth } = useAuth(); // Retrieve vendorId from context
  const [inventory, setInventory] = useState({});
  const [filteredInventory, setFilteredInventory] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);
  const [updatedDetails, setUpdatedDetails] = useState({ price: '', stock: '' });
  const [showAddProduct, setShowAddProduct] = useState(false); // Manage AddProductPage visibility
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  // Currency formatter
  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  });

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const response = await axios.get(`/api/vendor/inventory/${auth.vendorId}`);
        const groupedData = response.data.reduce((acc, item) => {
          const category = item.productId.category || 'Uncategorized';
          if (!acc[category]) acc[category] = [];
          acc[category].push(item);
          return acc;
        }, {});
        setInventory(groupedData);
        setFilteredInventory(groupedData); // Initialize filtered inventory
      } catch (error) {
        console.error('Error fetching inventory:', error);
      }
    };

    fetchInventory();
  }, [auth.vendorId]);

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    if (!term) {
      setFilteredInventory(inventory);
      return;
    }

    const newFilteredInventory = {};
    Object.keys(inventory).forEach((category) => {
      const filteredProducts = inventory[category].filter(
        (product) =>
          product.productId.name.toLowerCase().includes(term) ||
          product.productId.brand.toLowerCase().includes(term)
      );
      if (filteredProducts.length) newFilteredInventory[category] = filteredProducts;
    });
    setFilteredInventory(newFilteredInventory);
  };

  const handleRowClick = (product) => {
    setSelectedProduct(product);
    setUpdatedDetails({ price: product.price, stock: product.stock });
    setShowSlideIn(true);
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`/api/vendor/inventory/${selectedProduct._id}`, updatedDetails, {
        headers: { Authorization: `Bearer ${auth.token}` },
      });
      // Update local inventory state
      setInventory((prevInventory) => {
        const updatedInventory = { ...prevInventory };
        const category = selectedProduct.productId.category || 'Uncategorized';
        updatedInventory[category] = updatedInventory[category].map((item) =>
          item._id === selectedProduct._id
            ? { ...item, price: updatedDetails.price, stock: updatedDetails.stock }
            : item
        );
        return updatedInventory;
      });
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error updating product details:', error);
    }
  };

  const handleStatusToggle = async () => {
    try {
      const newStatus = selectedProduct.status === 'Active' ? 'Disabled' : 'Active';
      await axios.patch(`/api/vendor/inventory/${selectedProduct._id}/status`, { status: newStatus });
      setInventory((prev) => {
        const updated = { ...prev };
        const category = selectedProduct.productId.category || 'Uncategorized';
        updated[category] = updated[category].map((item) =>
          item._id === selectedProduct._id ? { ...item, status: newStatus } : item
        );
        return updated;
      });
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/vendor/inventory/${selectedProduct._id}`);
      setInventory((prev) => {
        const updated = { ...prev };
        const category = selectedProduct.productId.category || 'Uncategorized';
        updated[category] = updated[category].filter((item) => item._id !== selectedProduct._id);
        return updated;
      });
      setShowDeleteModal(false);
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };


  return (
    <div>
      <div className="d-flex align-items-center justify-content-between mb-3">
        {/* Search Bar */}
        <InputGroup className="w-75">
          <FormControl
            placeholder="Search by brand or product name"
            value={searchTerm}
            onChange={handleSearch}
          />
        </InputGroup>
        {/* Add Product Button */}
        <Button variant="success" onClick={() => setShowAddProduct(true)}>
          + Add Product
        </Button>
      </div>
      {Object.keys(filteredInventory).map((category) => (
        <div key={category}>
          <h4 className="mt-4">{category}</h4>
          <ListGroup>
            {filteredInventory[category].map((product) => (
              <ListGroup.Item
                key={product._id}
                className="d-flex justify-content-between align-items-center"
                style={{ cursor: 'pointer' }}
                onClick={() => handleRowClick(product)}
              >
                {/* Thumbnail and Details */}
                <div className="d-flex align-items-center">
                  <img
                    src={product.productId.imageUrl}
                    alt={product.productId.name}
                    className="me-3"
                    style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                  />
                  <div>
                    <div className="fw-bold">{product.productId.brand}</div>
                    <div className="text-muted">{product.productId.name || 'No Strain'}</div>
                    <Badge bg={product.stock === 0 ? 'secondary' : product.status === 'Active' ? 'success' : 'secondary'}>
                        {product.stock === 0 ? 'Out of Stock' : product.status}
                    </Badge>
                  </div>
                </div>
                {/* Price and Arrow */}
                <div className="d-flex align-items-center">
                  <span className="fw-bold">{formatter.format(product.price)}</span>
                  <i className="bi bi-chevron-right ms-2 fs-4"></i>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </div>
      ))}

      {/* Slide-in Window for Product Details */}
      {selectedProduct && (
        <Offcanvas show={showSlideIn} onHide={() => setShowSlideIn(false)} placement="end">
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Update Product</Offcanvas.Title>
            <Button
                variant={selectedProduct.status === 'Active' ? 'secondary' : 'success'}
                onClick={handleStatusToggle}
              >
                {selectedProduct.status === 'Active' ? 'Disable' : 'Enable'}
              </Button>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <div>
              <h5>{selectedProduct.productId.name}</h5>
              <p className="text-muted">{selectedProduct.productId.description || 'No Description'}</p>
              <img
                src={selectedProduct.productId.imageUrl}
                alt={selectedProduct.productId.name}
                style={{ width: '100%', height: '200px', objectFit: 'cover' }}
              />
            </div>
            <Form className="mt-4">
              <Form.Group className="mb-3">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  value={updatedDetails.price}
                  onChange={(e) => setUpdatedDetails({ ...updatedDetails, price: e.target.value })}
                  placeholder="Enter price"
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Stock</Form.Label>
                <Form.Control
                  type="number"
                  value={updatedDetails.stock}
                  onChange={(e) => setUpdatedDetails({ ...updatedDetails, stock: e.target.value })}
                  placeholder="Enter stock"
                />
              </Form.Group>
              <Button variant="primary" onClick={handleUpdate}>
                Save Changes
              </Button>
              <Button variant="danger" onClick={() => setShowDeleteModal(true)}>
                Delete Product
              </Button>
            </Form>
          </Offcanvas.Body>
        </Offcanvas>
      )}

      {/* Confirm Delete Modal */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Deletion</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete this product?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Slide-in Window for Adding Products */}
      <Offcanvas show={showAddProduct} onHide={() => setShowAddProduct(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Add Product</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <AddProductPage /> {/* Embed AddProductPage */}
        </Offcanvas.Body>
      </Offcanvas>
    </div>
  );
};

export default InventoryComponent;
