import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext'; // Adjust path as necessary

const ProductCard = ({ product, vendorName, logoUrl }) => {
  const { addToCart } = useContext(CartContext);

  return (
    <div className="card product-card shadow-sm" style={{ width: '14rem', position: 'relative' }}>
      {/* SALE Banner */}
      {product.salePrice && (
        <div
          className="position-absolute top-0 start-0 w-100 text-center"
          style={{
            backgroundColor: '#FF0080',
            color: 'white',
            fontSize: '0.8rem',
            padding: '0.3rem 0',
            fontWeight: 'bold',
          }}
        >
          SALE
        </div>
      )}

      {/* Card Body */}
      <div className="card-body d-flex flex-column justify-content-between">
        {/* Top Section: Category and Amount */}
        <div className="d-flex justify-content-between">
          <span className="text-muted" style={{ fontSize: '0.8rem' }}>
            {product.category}
          </span>
          <span className="badge bg-light text-dark" style={{ fontSize: '0.75rem' }}>
            {product.variations?.[0]?.amount || 'N/A'}
          </span>
        </div>

        {/* Product Image */}
        <div className="text-center my-3">
          <img
            src={product.images[0] || '/placeholder-image.png'}
            alt={product.name}
            className="img-fluid"
            style={{ maxHeight: '120px', objectFit: 'contain' }}
          />
        </div>

        {/* Product Info */}
        <div>
          {/* Brand and Strain */}
          <h6 className="card-title" style={{ fontSize: '0.9rem', lineHeight: '1.2' }}>
            <strong>{product.brand}</strong>
          </h6>
          <p style={{ fontSize: '0.85rem', fontWeight: '500', margin: 0 }}>
            {product.variations?.[0]?.strain || 'Unknown Strain'}
          </p>
          {/* Product Name */}
          <p className="text-muted" style={{ fontSize: '0.8rem' }}>
            {product.name}
          </p>

          {/* THC Content */}
          {product.variations && product.variations.length > 0 ? (
            <div className="border p-2 mt-2 text-center" style={{ fontSize: '0.85rem' }}>
              THC: {product.variations[0].thcContent}%
            </div>
          ) : (
            <p className="text-muted" style={{ fontSize: '0.85rem' }}>
              No variations available
            </p>
          )}
        </div>

        {/* Footer Section */}
        <div className="d-flex justify-content-between align-items-center mt-3">
          {/* Price Section */}
          <div>
            {product.salePrice ? (
              <>
                <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                  ${product.salePrice.toFixed(2)}
                </span>{' '}
                <span
                  style={{
                    textDecoration: 'line-through',
                    color: 'gray',
                    fontSize: '0.85rem',
                  }}
                >
                  ${product.price.toFixed(2)}
                </span>
              </>
            ) : (
              <span style={{ fontWeight: 'bold' }}>${product.price?.toFixed(2) || 'N/A'}</span>
            )}
          </div>

          {/* Add to Cart Button */}
          <button
            className="btn btn-primary rounded-circle"
            style={{
              width: '2.5rem',
              height: '2.5rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() =>
              addToCart({
                ...product,
                vendorName,
                logoUrl,
              })
            }
          >
            <i className="bi bi-plus-lg"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
