import React, { useState } from 'react';

const DeliveryAddress = ({ onSaveAddress }) => {
  const [address, setAddress] = useState({
    street: '',
    city: '',
    county: '',
    state: '',
    zip: '',
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddress((prevAddress) => ({
      ...prevAddress,
      [name]: value,
    }));
  };

  // Format the address to match the API's expected structure and pass it back to parent component
  const handleSave = () => {
    const formattedAddress = {
      street: address.street.toUpperCase(),
      city: address.city.replace(/\s+/g, '_').toUpperCase(),
      county: address.county,
      state: address.state.toUpperCase(),
      zip: address.zip,
    };
    onSaveAddress(formattedAddress);
  };

  return (
    <div className="delivery-address">
      <h3>Delivery Address</h3>
      <div className="form-group">
        <label>Street Address</label>
        <input
          type="text"
          name="street"
          value={address.street}
          onChange={handleChange}
          className="form-control"
          placeholder="Enter street address"
        />
      </div>
      <div className="form-group">
        <label>City</label>
        <input
          type="text"
          name="city"
          value={address.city}
          onChange={handleChange}
          className="form-control"
          placeholder="Enter city"
        />
      </div>
      <div className="form-group">
        <label>County</label>
        <input
          type="text"
          name="county"
          value={address.county}
          onChange={handleChange}
          className="form-control"
          placeholder="Enter county"
        />
      </div>
      <div className="form-group">
        <label>State</label>
        <input
          type="text"
          name="state"
          value={address.state}
          onChange={handleChange}
          className="form-control"
          placeholder="Enter state (e.g., CA)"
        />
      </div>
      <div className="form-group">
        <label>ZIP Code</label>
        <input
          type="text"
          name="zip"
          value={address.zip}
          onChange={handleChange}
          className="form-control"
          placeholder="Enter ZIP code"
        />
      </div>
      <button type="button" className="btn btn-primary" onClick={handleSave}>
        Save Address
      </button>
    </div>
  );
};

export default DeliveryAddress;
