import React, { useEffect, useState } from 'react';
import VendorCard from './VendorCard';
import { Form, InputGroup } from 'react-bootstrap';
import { GeoAltFill } from 'react-bootstrap-icons';
import { useNavigate } from 'react-router-dom';
import PickupMap from './PickupMap';
import './NearbyPickupPage.css';

const NearbyPickupPage = () => {
  const [vendors, setVendors] = useState([]);
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' });
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [cardPosition, setCardPosition] = useState('middle'); // 'middle' or 'full'
  const [dragStartY, setDragStartY] = useState(null);
  const [currentY, setCurrentY] = useState(0); // Dynamic drag position

  const navigate = useNavigate();
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const fetchVendors = async () => {
    // Fetch vendors implementation (unchanged for brevity)
  };

  const handleDragStart = (e) => {
    const startY = e.touches ? e.touches[0].clientY : e.clientY;
    setDragStartY(startY);
    setCurrentY(startY);
  };

  const handleDragMove = (e) => {
    if (dragStartY === null) return;

    const newY = e.touches ? e.touches[0].clientY : e.clientY;
    setCurrentY(newY);
  };

  const handleDragEnd = () => {
    if (dragStartY === null) return;

    const dragDistance = currentY - dragStartY;

    // Determine new card position based on drag distance
    if (dragDistance > 50) {
      setCardPosition('full'); // Dragged down
    } else if (dragDistance < -50) {
      setCardPosition('middle'); // Dragged up
    }

    // Reset drag state
    setDragStartY(null);
    setCurrentY(0);
  };

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }
  }, []);

  useEffect(() => {
    fetchVendors();
  }, []);

  return (
    <div className="container-fluid p-0">

      {/* Map Section */}
      <PickupMap
        dispensaries={vendors}
        onMarkerClick={(vendor) => {
          setSelectedVendor(vendor);
          setCardPosition('middle');
        }}
      />

      {/* Sliding Vendor Card */}
      {selectedVendor && (
        <div
          className={`vendor-card-container ${cardPosition}`}
          style={{
            transform: `translateY(${cardPosition === 'full' ? '0%' : '50%'})`,
            transition: dragStartY ? 'none' : 'transform 0.3s ease',
          }}
          onMouseDown={handleDragStart}
          onMouseMove={handleDragMove}
          onMouseUp={handleDragEnd}
          onTouchStart={handleDragStart}
          onTouchMove={handleDragMove}
          onTouchEnd={handleDragEnd}
        >
          <VendorCard key={selectedVendor._id} vendor={selectedVendor} />
        </div>
      )}
    </div>
  );
};

export default NearbyPickupPage;
