import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useContext(CartContext);

  return (
    <div className="card product-card shadow-sm">
      {product.discountedPrice && (
        <div
          className="position-absolute top-0 start-0 w-100 text-center"
          style={{
            backgroundColor: '#FF0080',
            color: '#fff',
            fontSize: '0.75rem',
            padding: '0.2rem',
          }}
        >
          SALE
        </div>
      )}
      <div className="card-body">
        {/* Brand and Name */}
        <h6 className="card-title">
          <strong>{product.brand}</strong> | {product.name}
        </h6>
        {/* Category */}
        <p className="text-muted" style={{ fontSize: '0.85rem' }}>
          {product.category}
        </p>
        {/* Product Image */}
        <div className="text-center">
          <img
            src={product.primaryImage}
            alt={product.name}
            className="img-fluid"
            style={{ maxHeight: '120px', objectFit: 'contain' }}
          />
        </div>
        {/* Variation */}
        <p className="mt-2">{product.variation}</p>
        {/* Amount */}
        <span className="badge border text-secondary">
          {product.amount} {product.uom}
        </span>
        <div className="d-flex justify-content-between align-items-center mt-3">
          {/* Price */}
          <div>
            {product.discountedPrice ? (
              <>
                <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                  ${product.discountedPrice.toFixed(2)}
                </span>{' '}
                <span
                  style={{
                    textDecoration: 'line-through',
                    color: '#6c757d',
                  }}
                >
                  ${product.price.toFixed(2)}
                </span>
              </>
            ) : (
              <span style={{ fontWeight: 'bold' }}>${product.price.toFixed(2)}</span>
            )}
          </div>
          {/* Add to Cart Button */}
          <button
            className="btn btn-primary rounded-circle"
            style={{
              width: '2.5rem',
              height: '2.5rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() => addToCart(product)}
          >
            +
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
