import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { ListGroup } from 'react-bootstrap';
import { AuthContext } from '../context/AuthContext';

const TestDashboard = () => {
  const { auth } = useContext(AuthContext); // Retrieve token and vendorId from context
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      if (!auth.vendorId) {
        console.error('vendorId is not defined in AuthContext');
        return;
      }

      try {
        const response = await axios.get(`/api/orders/vendor/${auth.vendorId}`, {
          headers: { Authorization: `Bearer ${auth.token}` }, // Pass token if route is protected
        });
        console.log('Orders:', response.data); // Debugging orders
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching vendor orders:', error);
      }
    };

    fetchOrders();
  }, [auth.vendorId, auth.token]); // Ensure useEffect runs when vendorId/token changes

  return (
    <div>
      <h2>Recent Orders</h2>
      <ListGroup>
        {orders.map((order) => (
          <ListGroup.Item key={order._id}>
            <p><strong>Order ID:</strong> {order._id}</p>
            <p><strong>Total:</strong> ${order.total ? order.total.toFixed(2) : 'N/A'}</p>
            <p><strong>Status:</strong> {order.status}</p>
            <p><strong>Delivery Address:</strong> 
              {order.deliveryAddress 
                ? `${order.deliveryAddress.street}, ${order.deliveryAddress.city}, ${order.deliveryAddress.state}, ${order.deliveryAddress.zip}`
                : 'N/A'}
            </p>
            <p><strong>Items:</strong></p>
            <ul>
              {order.items.map((item, index) => (
                <li key={index}>
                  {item.name} x {item.quantity} (${item.price ? item.price.toFixed(2) : 'N/A'})
                </li>
              ))}
            </ul>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default TestDashboard;
