import React, { useContext, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { VendorProvider, VendorContext } from '../context/VendorContext';
import VendorDetails from './VendorDetails';
import MenuDisplay from './MenuDisplay';
import CategoryFilter from './CategoryFilter';

const VendorMenuPage = () => {
  const { vendorId } = useParams();

  return (
    <VendorProvider vendorId={vendorId}>
      <VendorMenuContent />
    </VendorProvider>
  );
};

const VendorMenuContent = () => {
  const { vendor, products, loading, error } = useContext(VendorContext);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    // Filter products based on selected category
    if (selectedCategory === 'All') {
      setFilteredProducts(products);
    } else {
      setFilteredProducts(products.filter((product) => product.category === selectedCategory));
    }
  }, [selectedCategory, products]);

  const categories = ['All', 'Flower', 'Vape', 'Edible', 'Pre-roll', 'Topical']; // Define your categories here

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div
      className="vendor-menu-page"
      style={{
        position: 'relative', // Ensure isolated stacking context
        zIndex: 0, // Lower z-index for this section
      }}
    >
      <VendorDetails vendor={vendor} />
      <div
        style={{
          position: 'relative', // Isolate stacking context for CategoryFilter
          zIndex: 10, // Ensure CategoryFilter and dropdown stay above
        }}
      >
        <CategoryFilter
          categories={categories}
          selectedCategory={selectedCategory}
          onCategoryChange={handleCategoryChange}
        />
      </div>
      <div
        style={{
          position: 'relative',
          zIndex: 0, // MenuDisplay has a slightly higher z-index within this section
        }}
      >
        <MenuDisplay products={filteredProducts} />
      </div>
    </div>
  );
};

export default VendorMenuPage;
