import React, { useState } from 'react';
import MapComponent from './MapComponent';

function UpdateDeliveryZoneComponent({ isLoaded, loadError }) {
  const [deliveryZone, setDeliveryZone] = useState(null);

  const handleUpdateDeliveryZone = async () => {
    if (!deliveryZone || !deliveryZone.lat || !deliveryZone.lng) {
      alert('Please select a valid delivery zone before updating.');
      return;
    }
    const geoJsonDeliveryZone = {
        type: 'Point',
        coordinates: [deliveryZone.lng, deliveryZone.lat], // GeoJSON format
      };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('You are not logged in. Please log in and try again.');
        return;
      }

      const response = await fetch('/api/vendors/update-delivery-zone', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ deliveryZone: geoJsonDeliveryZone }),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Delivery zone updated successfully!');
      } else {
        alert(`Failed to update delivery zone: ${result.message}`);
      }
    } catch (error) {
      console.error('Error updating delivery zone:', error);
      alert('An error occurred while updating the delivery zone.');
    }
  };

  if (loadError) return <div>Error loading map</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <div>
      <h2>Set Delivery Zone</h2>
      <MapComponent onLocationChange={setDeliveryZone} />
      <div style={{ marginTop: '20px' }}>
        <label>
          Latitude:
          <input
            type="text"
            value={deliveryZone?.lat || ''}
            onChange={(e) =>
              setDeliveryZone((prev) => ({
                ...prev,
                lat: parseFloat(e.target.value),
              }))
            }
            style={{ marginLeft: '10px', marginRight: '20px' }}
          />
        </label>
        <label>
          Longitude:
          <input
            type="text"
            value={deliveryZone?.lng || ''}
            onChange={(e) =>
              setDeliveryZone((prev) => ({
                ...prev,
                lng: parseFloat(e.target.value),
              }))
            }
            style={{ marginLeft: '10px', marginRight: '20px' }}
          />
        </label>
        <button onClick={handleUpdateDeliveryZone} style={{ marginLeft: '10px' }}>
          Update Delivery Zone
        </button>
      </div>
    </div>
  );
}

export default UpdateDeliveryZoneComponent;
