import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const TestMenu = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch products from the backend
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                console.log("Fetched products:", response.data); // Log response to check structure
                setProducts(response.data.product_list || []); // Default to empty array if product_list is missing
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <Container>
            <h2 className="my-4">Product Menu</h2>

            {/* Display Products */}
            <Row>
                {products.map((product) => (
                    <Col xs={12} md={6} lg={4} key={product.product_id} className="mb-4">
                        <Card>
                            <Card.Img
                                variant="top"
                                src={product.e_commerce?.primary_image || '/placeholder.jpg'}
                                alt={product.product_configurable_fields.name}
                            />
                            <Card.Body>
                                <Card.Title>{product.product_configurable_fields.name}</Card.Title>
                                <Card.Text>{product.product_configurable_fields.brand}</Card.Text>
                                <Button variant="primary">
                                    ${product.pricing.price_sell} + Add to Cart
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default TestMenu;
