import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ListGroup } from 'react-bootstrap';

const TestDashboard = ({ vendorId }) => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`/api/orders/vendor/${vendorId}`);
        console.log('Orders:', response.data); // Debugging orders
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching vendor orders:', error);
      }
    };

    fetchOrders();
  }, [vendorId]);

  return (
    <div>
      <h2>Recent Orders</h2>
      <ListGroup>
        {orders.map((order) => (
          <ListGroup.Item key={order._id}>
            <p><strong>Order ID:</strong> {order._id}</p>
            <p><strong>Total:</strong> ${order.total ? order.total.toFixed(2) : 'N/A'}</p>
            <p><strong>Status:</strong> {order.status}</p>
            <p><strong>Delivery Address:</strong> 
              {order.deliveryAddress 
                ? `${order.deliveryAddress.street}, ${order.deliveryAddress.city}, ${order.deliveryAddress.state}, ${order.deliveryAddress.zip}`
                : 'N/A'}
            </p>
            <p><strong>Items:</strong></p>
            <ul>
              {order.items.map((item, index) => (
                <li key={index}>
                  {item.name} x {item.quantity} (${item.price ? item.price.toFixed(2) : 'N/A'})
                </li>
              ))}
            </ul>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default TestDashboard;
