import React from 'react';
//import './DeliveryPickupToggle.css';

const DeliveryPickupToggle = ({ selectedOption, setSelectedOption }) => (
  <div className="btn-group" role="group" aria-label="Delivery or Pickup toggle">
    {/* Delivery Button */}
    <input
      type="radio"
      className="btn-check"
      name="btnradio"
      id="btnradio1"
      autoComplete="off"
      checked={selectedOption === 'Delivery'}
      onChange={() => setSelectedOption('Delivery')}
    />
    <label
      className={`btn toggle-button ${selectedOption === 'Delivery' ? 'selected' : ''}`}
      htmlFor="btnradio1"
    >
      Delivery
    </label>

    {/* Pickup Button */}
    <input
      type="radio"
      className="btn-check"
      name="btnradio"
      id="btnradio3"
      autoComplete="off"
      checked={selectedOption === 'Pickup'}
      onChange={() => setSelectedOption('Pickup')}
    />
    <label
      className={`btn toggle-button ${selectedOption === 'Pickup' ? 'selected' : ''}`}
      htmlFor="btnradio3"
    >
      Pickup
    </label>
  </div>
);

export default DeliveryPickupToggle;
