import React from 'react';
import { Card, Row, Col, Image } from 'react-bootstrap';
import VendorStatusDropdown from './VendorStatusDropdown';
import './VendorDetails.css'; // Import CSS file

const VendorDetails = ({ vendor }) => {
  if (!vendor || !vendor.businessHours) {
    return <p>Loading vendor details...</p>; // Handle uninitialized vendor object
  }

  return (
    <Card
      className="vendor-details my-2 jump-in-container"
      style={{
        backgroundColor: '#fafafa', // Light yellow background
        border: 'none', // Remove card border
        borderRadius: '0px',
      }}
    >
      <Row className="align-items-center">
        {/* Vendor Details */}
        <Col xs={12}>
          <div className="vendor-details-container">
            {/* Vendor Logo and Name */}
            <div className="d-flex align-items-center">
              {/* Vendor Logo */}
              <Image
                src={vendor.logoUrl || '/placeholder-logo.png'}
                alt={`${vendor.dispensaryName} logo`}
                className="vendor-logo rounded"
              />

              {/* Vendor Name */}
              <p className="vendor-name">{vendor.dispensaryName}</p>
            </div>

            {/* Vendor Status Dropdown */}
            <div className="vendor-status-dropdown">
              <VendorStatusDropdown vendor={vendor} />
            </div>
          </div>
        </Col>
      </Row>
    </Card>
  );
};

export default VendorDetails;
