import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuthState] = useState({
        token: null,
        vendorId: null,
        vendorName: null,
    });
    const [loading, setLoading] = useState(true); // Add loading state

    useEffect(() => {
        const initializeAuth = async () => {
            const storedAuth = JSON.parse(localStorage.getItem('auth'));
            if (storedAuth?.token) {
                try {
                    // Validate the token
                    const response = await axios.post('/api/auth/refresh-token', {}, {
                        headers: { Authorization: `Bearer ${storedAuth.token}` },
                    });
                    // If valid, update the token and auth state
                    const { accessToken } = response.data;
                    const updatedAuth = { ...storedAuth, token: accessToken };
                    setAuth(updatedAuth); // Update context and localStorage
                } catch (error) {
                    console.error('Token refresh failed:', error);
                    // If invalid, log out the user
                    localStorage.removeItem('auth');
                    setAuthState({ token: null, vendorId: null, vendorName: null });
                }
            }
            setLoading(false); // Set loading to false after initialization
        };

        initializeAuth();
    }, []);


    const setAuth = (authData) => {
        localStorage.setItem('auth', JSON.stringify(authData));
        setAuthState(authData);
        axios.defaults.headers.common['Authorization'] = `Bearer ${authData.token}`;
    };

    const logout = (navigate) => {
        localStorage.removeItem('auth');
        setAuthState({ token: null, vendorId: null, vendorName: null });
        delete axios.defaults.headers.common['Authorization'];
        navigate('/');
    };

    return (
        <AuthContext.Provider value={{ auth, setAuth, logout, loading }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = React.useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
