import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';

const BusinessHoursComponent = ({ initialHours, onUpdate }) => {
  const [businessHours, setBusinessHours] = useState(initialHours || {});

  const handleChange = (day, field, value) => {
    setBusinessHours((prev) => ({
      ...prev,
      [day]: {
        ...prev[day],
        [field]: value,
      },
    }));
  };

  const handleUpdateHours = () => {
    onUpdate(businessHours);
  };

  const days = [
    'monday',
    'tuesday',
    'wednesday',
    'thursday',
    'friday',
    'saturday',
    'sunday',
  ];

  return (
    <div className="container mt-4">
      <h3>Update Business Hours</h3>
      <Form>
        {days.map((day) => (
          <Row className="mb-3" key={day}>
            <Col sm={2} className="text-capitalize">
              <Form.Label>{day}</Form.Label>
            </Col>
            <Col sm={4}>
              <Form.Control
                type="time"
                value={businessHours[day]?.open || ''}
                onChange={(e) => handleChange(day, 'open', e.target.value)}
                placeholder="Opening Time"
              />
            </Col>
            <Col sm={4}>
              <Form.Control
                type="time"
                value={businessHours[day]?.close || ''}
                onChange={(e) => handleChange(day, 'close', e.target.value)}
                placeholder="Closing Time"
              />
            </Col>
          </Row>
        ))}
        <Button variant="primary" onClick={handleUpdateHours}>
          Update Hours
        </Button>
      </Form>
    </div>
  );
};

export default BusinessHoursComponent;
