import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext'; // Import AuthContext
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

const LogoutButton = () => {
    const { logout } = useContext(AuthContext); // Access the logout function from AuthContext
    const navigate = useNavigate(); // Hook to navigate the user after logout

    const handleLogout = async () => {
        try {
            await logout(); // Trigger the logout function from AuthContext
            navigate('/login'); // Redirect the user to the login page
        } catch (error) {
            console.error('Logout failed:', error.message); // Handle any errors during logout
        }
    };

    return (
        <button
            onClick={handleLogout}
            className="btn btn-danger"
            style={{ marginLeft: '10px' }}
        >
            Logout
        </button>
    );
};

export default LogoutButton;
