// File: src/components/TestButton.js

import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const TestButton = () => {
  const [loading, setLoading] = useState(false);

  const handleTestClick = async () => {
    setLoading(true);

    const testPayload = {
      phone: '5556666558', // Hardcoded test phone number for now
    };

    try {
      // Send request to the backend to invoke createCustomer function
      const response = await fetch('http://localhost:5001/api/create-customer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(testPayload),
      });

      const statusCode = response.status;
      const data = await response.json();

      // Display response and status code in a popup window
      alert(
        `Status Code: ${statusCode}\nResponse: ${JSON.stringify(data, null, 2)}`
      );
    } catch (error) {
      // Handle and display errors in a popup window
      alert(`Error occurred: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex justify-content-center mt-5">
      <Button
        variant="primary"
        onClick={handleTestClick}
        disabled={loading}
        className="test-api-button"
      >
        {loading ? 'Testing...' : 'Test API'}
      </Button>
    </div>
  );
};

export default TestButton;
