import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const VendorAuth = () => {
  const [showModal, setShowModal] = useState(false); // Control modal visibility
  const [step, setStep] = useState(1); // Registration step
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    // Registration fields
    dispensaryType: "",
    licenseState: "",
    licenseNumber: "",
    businessAddress: { street: "", city: "", state: "", zip: "" },
    dispensaryName: "",
    firstName: "",
    lastName: "",
    contactNumber: "",
    termsAccepted: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  const handleLogin = () => {
    // Handle vendor login logic
    console.log("Login data:", { email: formData.email, password: formData.password });
  };

  const handleRegisterSubmit = () => {
    // Handle vendor registration logic
    console.log("Registration data:", formData);
    setShowModal(false); // Close modal after registration
  };

  const nextStep = () => setStep((prev) => prev + 1);
  const prevStep = () => setStep((prev) => prev - 1);

  return (
    <div className="container mt-5">
      <h2 className="text-center">Vendor Login</h2>
      <div className="d-flex flex-column align-items-center">
        {/* Login Form */}
        <div className="form-group w-50">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group w-50">
          <label>Password:</label>
          <input
            type="password"
            className="form-control"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
        <button className="btn btn-primary w-50" onClick={handleLogin}>
          Login
        </button>
        <button className="btn btn-link w-50 mt-3" onClick={() => setShowModal(true)}>
          Register
        </button>
      </div>

      {/* Registration Modal */}
      <div
        className={`modal fade ${showModal ? "show d-block" : ""}`}
        tabIndex="-1"
        role="dialog"
        style={{ backgroundColor: "rgba(0, 0, 0, 0.5)" }}
      >
        <div className="modal-dialog modal-lg" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Vendor Registration</h5>
              <button
                type="button"
                className="close"
                onClick={() => setShowModal(false)}
              >
                <span>&times;</span>
              </button>
            </div>
            <div className="modal-body">
              {/* Multi-Step Form */}
              {step === 1 && (
                <div>
                  <h4>What services do you provide?</h4>
                  <div className="d-flex justify-content-between">
                    <button
                      className={`btn btn-outline-primary ${
                        formData.dispensaryType === "storefront" ? "active" : ""
                      }`}
                      onClick={() => setFormData({ ...formData, dispensaryType: "storefront" })}
                    >
                      Pickup
                    </button>
                    <button
                      className={`btn btn-outline-primary ${
                        formData.dispensaryType === "delivery" ? "active" : ""
                      }`}
                      onClick={() => setFormData({ ...formData, dispensaryType: "delivery" })}
                    >
                      Delivery
                    </button>
                    <button
                      className={`btn btn-outline-primary ${
                        formData.dispensaryType === "both" ? "active" : ""
                      }`}
                      onClick={() => setFormData({ ...formData, dispensaryType: "both" })}
                    >
                      Pickup & Delivery
                    </button>
                  </div>
                  <button className="btn btn-primary mt-3" onClick={nextStep}>
                    Next
                  </button>
                </div>
              )}

              {step === 2 && (
                <div>
                  <h4>Dispensary Info</h4>
                  <div className="form-group">
                    <label>License State:</label>
                    <select
                      name="licenseState"
                      className="form-control"
                      value={formData.licenseState}
                      onChange={handleChange}
                    >
                      <option value="">Select State</option>
                      <option value="CA">CA</option>
                      <option value="NV">NV</option>
                      {/* Add more states */}
                    </select>
                  </div>
                  <div className="form-group">
                    <label>License Number:</label>
                    <input
                      type="text"
                      className="form-control"
                      name="licenseNumber"
                      value={formData.licenseNumber}
                      onChange={handleChange}
                    />
                  </div>
                  <h5>Business Address</h5>
                  <div className="form-group">
                    <label>Street:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.businessAddress.street}
                      onChange={(e) =>
                        handleNestedChange("businessAddress", "street", e.target.value)
                      }
                    />
                  </div>
                  <button className="btn btn-secondary" onClick={prevStep}>
                    Back
                  </button>
                  <button className="btn btn-primary ml-3" onClick={nextStep}>
                    Next
                  </button>
                </div>
              )}

              {step === 3 && (
                <div>
                  <h4>Terms & Conditions</h4>
                  <div className="border p-3" style={{ height: "200px", overflowY: "scroll" }}>
                    <p>WeedHurry is not liable for...</p>
                  </div>
                  <div className="form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      onChange={(e) =>
                        setFormData({ ...formData, termsAccepted: e.target.checked })
                      }
                    />
                    <label className="form-check-label">I agree to the terms & conditions</label>
                  </div>
                  <button className="btn btn-secondary" onClick={prevStep}>
                    Back
                  </button>
                  <button
                    className="btn btn-primary ml-3"
                    onClick={() => {
                      if (formData.termsAccepted) handleRegisterSubmit();
                      else alert("Please accept the terms.");
                    }}
                  >
                    Complete Registration
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VendorRegistration;
