// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/NavBar';
import VendorMenu from './components/VendorMenu.js';
import VendorPage from './components/VendorPage';
import { CartProvider } from './context/CartContext';
import CheckoutPage from './components/CheckoutPage';
import VendorRegistration from './components/VendorRegistration';
import VendorDashboard from './components/VendorDashboard';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import SignIn from './components/SignIn';
import Menu from './components/Menu';

function App() {
  return (
    <AuthProvider> {/* Wrap the entire app in AuthProvider for auth context */}
      <CartProvider>
        <Router>
          <Navbar /> {/* Navbar can access cart and auth context */}
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<VendorPage />} /> {/* Home - List of vendors */}
            <Route path="/vendor/:id" element={<VendorMenu />} /> {/* Individual vendor's page */}
            <Route path="/checkout" element={<CheckoutPage />} />
            <Route path="/register" element={<VendorRegistration />} />
            <Route path="/sign-in" element={<SignIn />} />

            {/* Protected Routes */}
            <Route element={<ProtectedRoute />}>
              <Route path="/vendor-dashboard" element={<VendorDashboard />} />
            </Route>
          </Routes>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
