import React, { useState, useContext } from 'react';
import { Button, Form, Row, Col, Card } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const PlaceOrder = () => {
  const { calculateTotal, cartItems } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    day: '',
    month: '',
    year: '',
  });

  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const [loading, setLoading] = useState(false);

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  // Function to extract vendorId from cart items
  const getVendorIdFromCart = () => {
    if (cartItems.length === 0) return null;

    const uniqueVendors = new Set(cartItems.map((item) => item.vendorId));
    if (uniqueVendors.size > 1) {
      alert('Your cart contains items from multiple vendors. Please place separate orders.');
      return null;
    }

    return [...uniqueVendors][0]; // Return the single vendorId
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress({ ...deliveryAddress, [name]: value });
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.phone || !contactInfo.firstName || !deliveryAddress.street) {
      alert('Please fill all required fields');
      return;
    }

    const vendorId = getVendorIdFromCart();
    if (!vendorId) {
      return;
    }

    const customerData = {
      ...contactInfo,
      birthdate: `${contactInfo.year}-${String(contactInfo.month).padStart(2, '0')}-${String(contactInfo.day).padStart(2, '0')}`,
    };

    const orderDetails = {
      vendorId,
      customerData,
      deliveryAddress,
      products: cartItems.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
        sizeId: item.sizeId || null,
      })),
      totalAmount: estimatedTotal.toFixed(2),
    };

    setLoading(true);
    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(orderDetails),
      });

      if (response.ok) {
        const data = await response.json();
        alert('Order placed successfully!');
        console.log('Order Response:', data);
      } else {
        const errorData = await response.json();
        alert(`Order failed: ${errorData.message}`);
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Failed to place order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="place-order-section">
      {/* Delivery Address Form */}
      <Form>
        <h4>Delivery Address</h4>
        <Form.Group controlId="street">
          <Form.Label>Street</Form.Label>
          <Form.Control
            type="text"
            name="street"
            value={deliveryAddress.street}
            onChange={handleAddressChange}
          />
        </Form.Group>
        <Row>
          <Col>
            <Form.Label>City</Form.Label>
            <Form.Control
              type="text"
              name="city"
              value={deliveryAddress.city}
              onChange={handleAddressChange}
            />
          </Col>
          <Col>
            <Form.Label>State</Form.Label>
            <Form.Control
              type="text"
              name="state"
              value={deliveryAddress.state}
              onChange={handleAddressChange}
            />
          </Col>
          <Col>
            <Form.Label>ZIP Code</Form.Label>
            <Form.Control
              type="text"
              name="zip"
              value={deliveryAddress.zip}
              onChange={handleAddressChange}
            />
          </Col>
        </Row>

        {/* Contact Information Form */}
        <h4>Contact Information</h4>
        <Row>
          <Col>
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              name="firstName"
              value={contactInfo.firstName}
              onChange={handleInputChange}
            />
          </Col>
          <Col>
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              type="text"
              name="lastName"
              value={contactInfo.lastName}
              onChange={handleInputChange}
            />
          </Col>
        </Row>
        <Row>
          <Col>
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={contactInfo.email}
              onChange={handleInputChange}
            />
          </Col>
          <Col>
            <Form.Label>Phone</Form.Label>
            <Form.Control
              type="text"
              name="phone"
              value={contactInfo.phone}
              onChange={handleInputChange}
            />
          </Col>
        </Row>
      </Form>

      {/* Order Summary */}
      <h4>Order Summary</h4>
      <Card>
        <Card.Body>
          <p>Subtotal: ${subtotal.toFixed(2)}</p>
          <p>Estimated Total (w/ Taxes): ${estimatedTotal.toFixed(2)}</p>
        </Card.Body>
      </Card>

      {/* Place Order Button */}
      <Button onClick={handlePlaceOrder} disabled={loading}>
        {loading ? 'Placing Order...' : 'Place Order'}
      </Button>
    </div>
  );
};

export default PlaceOrder;
